sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val verdiFraKontrolliste = hentKontrolliste(
                   skattemelding.kontrollKontekst.inntektsaar,
                   "kontrolliste - Fritidseiendom utleie",
                   listeAv(partsnr))
           .forForekomsterAv { verdi }
           .verdiFor { verdi1 }    

        // kontrollen gir utslag dersom skattyters partsnr finnes i kontrollisten
        // og det finnes grunnlag for utleie fra utleiemegler
        // AB-test: hvis verdi i  listen = A, skal merknaden vises for skattyter
        // skal bare gi utslag dersom skattyter ikke har lagt til mer utleieinntekt enn det som finnes i grunnlaget
        // skal ikke gi utslag dersom det finnes innsending pga sjekk mot grunnlag
        skattemelding
            .utslagFor {

               val sumGrunnlagUtleieFraUtleieformidler = forForekomsterAv(gruppe(regnskapsbehandletUtleie, regnskapsbehandletUtleieIUtlandet, sjablongbehandletUtleie, 
                                                          sjablongbehandletUtleieIUtlandet, sjablongOgRegnskapsbehandletUtleie))
                   .filtrer{grunnlag(utleieformidler) er oppgitt}
                   .summer {grunnlag(bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall)} 

               val sumFastsattUtleieFraUtleieformidler = forForekomsterAv(gruppe(regnskapsbehandletUtleie, regnskapsbehandletUtleieIUtlandet, sjablongbehandletUtleie, 
                                                          sjablongbehandletUtleieIUtlandet, sjablongOgRegnskapsbehandletUtleie))
                   .filtrer{fastsatt(utleieformidler) er oppgitt}
                   .summer {fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall)}    

               val diffUtleieFraUtleiemegler = sumGrunnlagUtleieFraUtleieformidler - sumFastsattUtleieFraUtleieformidler


                hvis(ikke(harInnsending) og
                     (verdiFraKontrolliste erLik "A") og 
                     (sumGrunnlagUtleieFraUtleieformidler erStørreEnn 0) og 
                     (diffUtleieFraUtleiemegler erStørreEnnEllerLik 0)) 
              {
                    betjeningsstrategiSettesTil(
                        ikke, merknadTips, skjulerSkatteberegning)
                }
            }
    }

