                hvis(
                    ikke(harInnsending) og
                        (
                            (grunnlag(antattVerdiSomNyttKjoeretoey.beloepSomTall) ikkeEr oppgitt) og (
                                (
                                    fastsatt(
                                        antattMarkedsverdiForKjoeretoey.beloepSomTall
