                                    ) eller (
                                    fastsatt(
                                        antattVerdiSomNyttKjoeretoey.beloepSomTall
                                    ) erStørreEnn 3_000_000
                                    )
                                )
                            )
                        eller
                        (
                            (grunnlag(antattVerdiSomNyttKjoeretoey.beloepSomTall) erMindreEnn 3_000_000) og (
                                (
                                    fastsatt(
                                        antattVerdiSomNyttKjoeretoey.beloepSomTall
                                    ) erStørreEnn 3_000_000
                                    ) eller (
                                    fastsatt(
                                        antattMarkedsverdiForKjoeretoey.beloepSomTall
                                    ) erStørreEnn 3_000_000
                                    )
