        skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .utslagFor {
                val pensjonMaanedMedMetodeForDobbeltbeskatning = forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst { pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning er oppgitt }

                val pensjonMaanedUtenMetodeForDobbeltbeskatning = forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst { (pensjonsinntektSomGruppe.beloepSomTall er oppgitt) og (pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning ikkeEr oppgitt) }

                hvis(pensjonMaanedMedMetodeForDobbeltbeskatning og pensjonMaanedUtenMetodeForDobbeltbeskatning) {
