sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad skattyter har lagt til andre kostnader knyttet til andre inntekter
        // med beskrivelse som inneholder ordene Minstefradrag, minste fradrag, personfradrag, forskuddstrekk,
        // finnmarksfradrag, fremførbart underskudd, Boligsparing for ungdom, BSU, skattetrekk,
        // lønnstrekk eller eiendomsskatt
        skattemelding
            .forForekomsterAv(minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt)
            .utslagFor {
                val harBeskrivelse = forForekomsterAv(kostnad)
                    .filtrer { fastsatt(kostnadstypeSomTekst) erLik "andreKostnaderKnyttetTilAnnenInntekt" }
                    .hvorMinstEnForekomst {
                        (beloepSomGruppe.beloepSomTall erStørreEnn 0) og
                            (
                                (beskrivelse.inneholder("minstefradrag")) eller
                                    (beskrivelse.inneholder("minste fradrag")) eller
                                    (beskrivelse.inneholder("personfradrag")) eller
                                    (beskrivelse.inneholder("forskuddstrekk")) eller
                                    (beskrivelse.inneholder("finnmarksfradrag")) eller
                                    (beskrivelse.inneholder("fremførbart underskudd")) eller
                                    (beskrivelse.inneholder("boligsparing for ungdom")) eller
                                    (beskrivelse.inneholder("BSU")) eller
                                    (beskrivelse.inneholder("skattetrekk")) eller
                                    (beskrivelse.inneholder("lønnstrekk")) eller
                                    (beskrivelse.inneholder("eiendomsskatt"))
                                )
                    }

                hvis(harBeskrivelse) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1, skjulerSkatteberegning)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Skattepliktig krever > 20000 i feltet "Andre kostnader knyttet til arbeidsinntekt" samt andre kostnader
        // knyttet til arbeidsinntekt + øvrige fradrag som er med og overstyrer minstefradrag > minstefradrag
           skattemelding
            .forForekomsterAv(minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt)
            .utslagFor {
                val sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag = 
                   fastsatt(fradragForKostnaderKnyttetTilArbeidSomGruppe.beloepUtenHensynTilValgtPrioritertFradragstype.beloepSomTall) - 
                   fastsatt(minstefradragIInntektSomGruppe.beloepUtenHensynTilValgtPrioritertFradragstype.beloepSomTall) - 20_000

                val harAndreKostnaderAnnenInntekt =
                   fastsatt(fradragForKostnaderKnyttetTilAnnenInntekt.fradragsberettigetBeloep.beloepSomTall)

                val harVedlegg =
                    forForekomsterAv(kostnad).hvorMinstEnForekomst { beloepSomGruppe.beloepIValuta har vedlegg }

                hvis(
                    ikke(harVedlegg) og
                       ((sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag erStørreEnnEllerLik 0) eller
                       (harAndreKostnaderAnnenInntekt erStørreEnnEllerLik 20_000))

                ) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1, skjulerSkatteberegning,
                                               Avviksbeloep settesTil sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag)
                }
            }
    }

