        val sumTapVedRealisasjonAvFastEiendomTilordnetUtlandet =
            forekomster.summer { tapVedRealisasjonAvFastEiendomTilordnetUtlandet }
        val sumTapVedRealisasjonAvVerdipapirTilordnetUtlandet =
            forekomster.summer { tapVedRealisasjonAvVerdipapirTilordnetUtlandet }
        val sumUnderskuddVedUtleieAvFastEiendomTilordnetUtlandet =
            forekomster.summer { underskuddVedUtleieAvFastEiendomTilordnetUtlandet }
        val sumSaerskiltFradragForFiskereOgFangstfolkTilordnetUtlandet =
            forekomster.summer { saerskiltFradragForFiskereOgFangstfolkTilordnetUtlandet }
        val sumSaerskiltFradragForSjoefolkTilordnetUtlandet =
            forekomster.summer { saerskiltFradragForSjoefolkTilordnetUtlandet }
        val sumFremfoertUnderskuddFraTidligereAarTilordnetUtlandet =
            forekomster.summer { fremfoertUnderskuddFraTidligereAarTilordnetUtlandet }

        // Kontrollen gir utslag og merknad dersom summen av kostnader tilknyttet utlandet (for kredit og alternativ fordeling) overstiger summen av kostnader i skattemeldingen
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    fradragDirekteTilordnetInntektFraUtlandet,
                    fradragDirekteTilordnetInntektFraUtlandetAlternativFordeling
                )
            )
            .utslagFor {

                hvis(
                    (sumMinstefradragIInntektUtlandet erStørreEnn oevrigTilVisning.minstefradragIInntektSomTall) eller
                        (sumFradragForKostnaderKnyttetTilArbeidUtlandet erStørreEnn oevrigTilVisning.fradragForKostnaderKnyttetTilArbeidSomTall) eller
                        (sumFradragForMerkostnadUtlandet erStørreEnn oevrigTilVisning.fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomTall) eller
                        (sumKostnaderKnyttetTilArbeidsOgBesoeksreiserTilordnetUtlandet erStørreEnn oevrigTilVisning.reisefradragSomTall) eller
                        (sumFagforeningskontingentTilordnetUtlandet erStørreEnn oevrigTilVisning.inntektsfradragForFagforeningskontingentSomTall) eller
                        (sumPensjonsinnbetalingTilPensjonsordningTilordnetUtlandet erStørreEnn oevrigTilVisning.samletPensjonsinnbetalingTilPensjonsordningIArbeidsforhold) eller
                        (sumTapVedRealisasjonAvFastEiendomTilordnetUtlandet erStørreEnn oevrigTilVisning.tapVedRealisasjonAvFastEiendomSomTall) eller
                        (sumTapVedRealisasjonAvVerdipapirTilordnetUtlandet erStørreEnn oevrigTilVisning.tapVedRealisasjonAvVerdipapir) eller
                        (sumUnderskuddVedUtleieAvFastEiendomTilordnetUtlandet erStørreEnn oevrigTilVisning.underskuddForAndelVedUtleieAvFastEiendom) eller
                        (sumSaerskiltFradragForFiskereOgFangstfolkTilordnetUtlandet erStørreEnn oevrigTilVisning.saerskiltFradragForFiskereOgFangstfolkSomTall) eller
                        (sumSaerskiltFradragForSjoefolkTilordnetUtlandet erStørreEnn oevrigTilVisning.saerskiltFradragForSjoefolkSomTall) eller
                        (sumFremfoertUnderskuddFraTidligereAarTilordnetUtlandet erStørreEnn oevrigTilVisning.fremfoertUnderskuddFraTidligereAarSomTall)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

