                            (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                    }

                hvis(ikke(harInnsending) og erKPP og harOppgittNyPensjonUtenUnntak) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadMangel, 1, skjulerSkatteberegning
