        // kontrollen gir utslag og merknad dersom det valgt metode for å unngå dobbeltbeskatning = kreditfradrag, men IKKE oppgitt skatt betalt i utlandet
        // skal IKKE gi utslag dersom trygdeEllerSkattemessigUnntak er satt til skattefri
        // skal ikke gi utslag hvis utslag på gitte kontroller
        skattemelding
            .utslagFor {
                val kortMedMetodeDobbeltbeskatningKreditUtenSkattefrittUnntak = forAlleForekomster
                    .hvorMinstEnForekomst {
                        (fastsatt(metodeVedDobbeltbeskatning) erLik "kredit") og
                            (
                                fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriUtenTrygdeavgiftsplikt",
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                                )
                                )
                    }

                hvis(ikke(harUtslagPaaKontroll) og 
                    kortMedMetodeDobbeltbeskatningKreditUtenSkattefrittUnntak og
                        (fastsatt(kravOmKreditfradragForUtenlandskSkatt) ikkeEr oppgitt) og
                        (fastsatt(kravOmKreditFradragFremfoertUtenlandskSkatt) ikkeEr oppgitt)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1, skjulerSkatteberegning)
                }
            }
    }

