            .utslagForFørste {

                val differanseAvkortetSkattPaaAksjeutbytte = skattPaaBruttoAksjeutbytte - avkortetSkattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap

                hvis(differanseAvkortetSkattPaaAksjeutbytte erStørreEnn 0) {
