sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val differanseMellomInnrapportertOgFastsattSjoemannsfradrag = skattemelding
            .forForekomsterAv(saerskiltFradragForSjoefolk)
            .hvorMinstEnForekomst {
                (fastsatt(fradragsberettigetBeloep.beloepSomTall) - grunnlag(fradragsberettigetBeloep.beloepSomTall)) erStørreEnn 0
            }

        // Kontrollen gir utslag dersom skattyter har økt særskilt fradrag for sjøfolk
        // Dette gjøres ved å øke lønn i lønnskortet og merke lønnen med girRettTilSaerskiltFradragForSjoefolk- merknaden skal derfor vises der
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer { fastsatt(girRettTilSaerskiltFradragForSjoefolk) erLik true }
            .utslagFor {

                val harEndretLønnSomGirRettTilSaerfradragForSjoefolk =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) - grunnlag(beloepSomGruppe.beloepSomTall)) erStørreEnn 0
                        }

                hvis(harEndretLønnSomGirRettTilSaerfradragForSjoefolk og differanseMellomInnrapportertOgFastsattSjoemannsfradrag) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
