sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

         val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = skattemelding
            .forForekomsterAv(sti(skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler, aarsakTilEndring))
            .harIngenForekomsterDer { aarsak erLik "overfortTilFraEktefelle" }

        // kontrollen gir utslag når skattepliktige reduserer forhåndsutfylt kundeutbytte, endring over kr 1 000,
        // og har ikke oppgitt at det er overførst fra ektefelle/samboer
        // kontrollen gir merknad i SME

        skattemelding
            .forForekomsterAv(skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler)
            .filtrer {
                (grunnlag(utbetaltBeloep.beloepSomTall) er oppgitt) 
            }
            .utslagFor {
                val differanseKundeutbytte =
                    grunnlag(utbetaltBeloep.beloepSomTall) - fastsatt(
                            utbetaltBeloep.beloepSomTall
                        )

            hvis( ikke(harInnsending) og oppgittEndringsaarsakIkkeErOverførtFraEktefelle og
                     (differanseKundeutbytte erStørreEnn 1_000)
              ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("LONN_GLD_ULIK_FASTSETTING_NULLING")

        //kontrollen gir utslag og merknad dersom skattyter har endret preutfylt lønn med mer enn 100
        //unntatt noen godtgjørelser som vanligvis kan korrigeres
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer { grunnlag(arbeidsgiversNavn) er oppgitt }
            .utslagFor {
                val sumLoennPrArbeidsgiver = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer {
                        (
                            grunnlag(behandlingsart).finnesIkkeBlant(
                                "BILGODT",
                                "GODTGJ_FREMKOMST",
                                "UTGGJ_REISE_KOST_LOSJI",
                                "UTG_GODTGJORELSE"
                            )
                            ) og
                            (fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0)
                    }
                    .summer { beloepSomGruppe.beloepSomTall }

                val differanseLoenn =
                    differanse(grunnlag(sumLoennPrArbeidsgiver), fastsatt(sumLoennPrArbeidsgiver))

                hvis(
                    ikke(harUtslagPaaKontroll) og
                        (differanseLoenn erStørreEnn 100)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag og merknad dersom skattyter nuller ut eller fjerner preutfylt lønn
        // sjekker på at sum differanse mellom grunnlag og fastsatt på ytelser er større enn 0,
        // for å unngå at merknad vises dersom skattyter har først fjeret og så lagt tilbake en ytelse
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(arbeidsgiver)
            .filtrer { grunnlag(arbeidsgiversNavn) er oppgitt }
            .utslagFor {
                val ytelserSomErNullstiltEllerFjernet =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .filtrer {
                            grunnlag(behandlingsart).finnesIkkeBlant(
                                "BILGODT",
                                "GODTGJ_FREMKOMST",
                                "UTGGJ_REISE_KOST_LOSJI",
                                "UTG_GODTGJORELSE"
                            )
                        }
                        .hvorMinstEnForekomst {
                            (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og
                                (
                                    (fastsatt(beloepSomGruppe.beloepSomTall) erLik 0) eller (
                                        fastsatt(
                                            beloepSomGruppe.beloepSomTall
                                        ) ikkeEr oppgitt
                                        )
                                    )
                        }

                val sumYtelserIkkeGodtgjoerelser =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .filtrer {
                            grunnlag(behandlingsart).finnesIkkeBlant(
                                "BILGODT",
                                "GODTGJ_FREMKOMST",
                                "UTGGJ_REISE_KOST_LOSJI",
                                "UTG_GODTGJORELSE"
                            )
                        }
                        .summer { beloepSomGruppe.beloepSomTall }

                val differanseSumYtelser =
                    differanse(grunnlag(sumYtelserIkkeGodtgjoerelser), fastsatt(sumYtelserIkkeGodtgjoerelser))

                hvis(ikke(harInnsending) og ytelserSomErNullstiltEllerFjernet og (differanseSumYtelser erStørreEnn 0)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harOppgittTrygdefriLoenn = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer { fastsatt(saerskiltSkattepliktSomTekst) erLik "skattepliktEtterPetroleumsskatteloven" }
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForHelsedel") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriUtenTrygdeavgiftsplikt") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForHelsedel ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForPensjonsdel ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada  ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftsplikt")
            }

        // kontrollen gir utslag dersom skattyter har oppgitt tilleggsinformasjon under andre forhold for petroleumsskatteloven
        // (har svar Ja eller Delvis på spørsmål om lønnen er opptjent om bord på utenlandsk skip i fart på norsk sokkel)
        // gir bare utslag dersom skattyter ikke har oppgitt trygdemessig unntak på lønn som er merket med særskilt skatteplikt for petroleumskatteloven
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(tilleggsinformasjonVedSaerskiltSkattepliktEtterPetroleumsskatteloven)
            .utslagFor {
                val harOppgittTilleggsinformasjon = hvorMinstEnForekomst {
                    arbeiderPaaUtenlandskRegistrertSkipIFartPaaNorskSokkel.finnesBlant(
                        "ja",
                        "delvis"
                    )
                }

                hvis(harOppgittTilleggsinformasjon og ikke(harOppgittTrygdefriLoenn)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har lagt til en av disse endringsårsakene på lønn, men IKKE har oppgitt noe skattemessig unntak og ikke dobbeltbeskatningsmetode på lønnen
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer {
                forForekomsterAv(aarsakTilEndring)
                    .hvorMinstEnForekomst {
                        fastsatt(aarsak).finnesBlant(
                            "endretTilOpptjentIUtlandet",
                            "begrensetSkattepliktig",
                            "arbeidsinntektINorgeSomIkkeErSkattepliktig"
                        )
                    }
            }
            .utslagFor {
                val harInntektUtenSkattemessigUnntakOgUtenDobbeltbeskatningsmetode =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (beloepSomGruppe.beloepSomTall er oppgitt) og (fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) ikkeEr oppgitt) og
                                (
                                    fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                        "skattefriMedTrygdeavgiftsplikt",
