sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val skattepliktForekomster =  skattepliktGjeldende
            .forForekomsterAv(skatteplikt) 

        val partsnr = skattepliktForekomster
            .verdiFor { partsnummerSomTekst }

        val harGlobalSkatteplikt = skattepliktForekomster
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }       

        // Kontrollen gir utslag og merknad dersom skattyter har en utenlandsk eiendom i kontrollisten
        // og skattyter IKKE har oppgitt noen formue av utenlandsk eiendom i skattemeldingen
        //gir bare utslag dersom skattyter har global skatteplikt
        skattemelding
            .utslagFor {

                val utenlandskEiendomIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - eiendommer i Utlandet",
                    listeAv(partsnr)
                )

                val eiendomIUtlandet = forForekomsterAv(fastEiendom)
                  .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
                  .verdierFor { internEiendomsidentifikator }

                val harOppgittFormueAvEiendomIUtlandet = forForekomsterAv(gruppe(
                      formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                      formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                      formuesgrunnlagBoenhetIBoligselskap,
                      formuesgrunnlagBorett,
                      formuesgrunnlagEgenFritaksbehandletBolig,
                      formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                      formuesgrunnlagFlerboligbygning,
                      formuesgrunnlagGaardsbruk,
                      formuesgrunnlagIkkeUtleidNaeringseiendomIUtlandet,
                      formuesgrunnlagRegnskapsbehandletBolig,
                      formuesgrunnlagRegnskapsbehandletFritidseiendom,
                      formuesgrunnlagSelveidBolig,
                      formuesgrunnlagSelveidFritidseiendom,
                      formuesgrunnlagSkogeiendomIUtlandet,
                      formuesgrunnlagTomt,
                      formuesgrunnlagUtleidFlerboligbygningIUtlandet,
                      formuesgrunnlagUtleidNaeringseiendom))
                  .hvorMinstEnForekomst {
                      (fastsatt(formuesverdi.beloepSomTall) er oppgitt) og
                      (internEiendomsidentifikator.finnesBlant(eiendomIUtlandet)) }

                hvis(harGlobalSkatteplikt og
                     ikke(harOppgittFormueAvEiendomIUtlandet) og
                     utenlandskEiendomIkontrolliste
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val alleVerdierFraKontrolliste = hentKontrolliste(
            skattemelding.kontrollKontekst.inntektsaar,
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr)
        )
            .forForekomsterAv { verdi }
            .alleVerdier

        val harUtslagPaaKontroll = utslagPåKontroll("ENDRINGSÅRSAK_SKATTEMESSIG_EMIGRASJON_SMIA") eller
            utslagPåKontroll("ENDRINGSÅRSAK_BOSATT_ETTER_AVTALE_MED_METODE_FOR_DOBBELTBESKATNING") eller
            utslagPåKontroll("ENDRINGSÅRSAK_BOSATT_ETTER_AVTALE_UTEN_METODE_FOR_DOBBELTBESKATNING") eller
            utslagPåKontroll("KORT_BOSATT_ETTER_AVTALE_UTEN_METODE_FOR_DOBBELTBESKATNING") eller
            utslagPåKontroll("BOSATT_ETTER_SKATTEAVTALE_UTEN_SKATTEMESSIG_UNNTAK_INFOBREV") eller
            utslagPåKontroll("KORT_SKATTEMESSIG_EMIGRASJON_SMIA") eller
            utslagPåKontroll("DOBLEKRAV_AVTALEBOSTED_EMIGRASJON")

        val harSaerskiltSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst {
                fastsatt(type).finnesBlant(
                    "kildeskattepliktPaaLoenn",
                    "kildeskattepliktPaaPensjon",
                    "skattepliktSomUtenrikstjenestemann",
                    "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato",
                    "skattepliktAvNaeringsdriftEiendomMv"
                )
            }

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val harFastsattHoyerePersoninntektEnnGrunnlag = beregnetSkattDokument
            .hvorMinstEnForekomst {
                (fastsatt(beregnetSkattSomGruppe.skattOgAvgift.trinnskatt.grunnlag) - grunnlag(
                    beregnetSkattSomGruppe.skattOgAvgift.trinnskatt.grunnlag
                )) erStørreEnn 0
            }

        /* val sumEktefellesInntekt = skattemeldingEktefelle
           .forForekomsterAv(summertSkattegrunnlagForVisning)
           .forForekomsterAv(sumInntekterIAlminneligInntektFoerFordelingsfradrag_SKVIS)
           .summer{fastsatt(beloep_SKVIS)} */ //TODO - når vi får på plass sjekk mot ektefelles skattemelding i SME

        //kontrollen gir utslag dersom skattepliktiges partsnr finnes i kontrollisten
        //hvis skattepliktig ifølge KL er medlem i folketrygden (uavhengig av type og lengde på medlemskap)
        //og IKKE har norsk pensjon/uføretrygd og heller IKKE har lønn/næringsinntekt med opptjeningsland utland.
        //skal ikke gi utslag dersom sum fastsatt personinntekt er større enn grunnlaget
        //skal ikke ha utslag hvis skattyter har særskatteplikt eller begrenset skatteplikt
        //skal ikke gi utslag dersom skattyter har utslag på nevnte kontroller
        //skal ikke ha utslag hvis ektefelle har høy inntekt (sum inntekt i oppsummering NOK 1 100 000 eller mer)

        skattemelding
            .utslagFor {
                val loennIUtlandet = forForekomsterAv(arbeidsgiver)
                    .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt)
                    }

                val pensjonINorge = forForekomsterAv(pensjonsinntekt)
                    .filtrer { (fastsatt(landkode) ikkeEr oppgitt) eller (fastsatt(landkode) erLik "NO") }
                    .forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst {
                        (fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt)
                    }

                val naeringIUtlandet = forForekomsterAv(naeringsinntektMv)
                    .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
                    .hvorMinstEnForekomst {
                        (fastsatt(naeringsinntektTilordnetInnehaver.beloepSomTall) er oppgitt) eller
                            (fastsatt(underskuddTilordnetInnehaver.beloepSomTall) er oppgitt)
                    }


                hvis(
                    ikke(harUtslagPaaKontroll) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        (ikke(loennIUtlandet) og ikke(pensjonINorge) og ikke(naeringIUtlandet)) og
                        harFastsattHoyerePersoninntektEnnGrunnlag og
                        // (sumEktefellesInntekt erMindreEnn 1_100_000) og  //TODO - når vi får på plass sjekk mot ektefelles skattemelding i SME
                        ikke(harSaerskiltSkatteplikt) og
                        ikke(harBegrensetSkatteplikt)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadTips, 1
                    )
                }
            }
    }

sakstype(Personlig)
