sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val datoIInntektsaar = KonstantTekstFelt("2022-01-01").dato

        val haddePendleroppholdIFjor = fjoraaretsSkattemelding
            .forForekomsterAv(inntektsaar2021.pendleropphold)
            .hvorMinstEnForekomst {
                fastsatt(inntektsaar2021.beregnetKostnadForKostOgLosjiForPendleropphold.beloepSomTall) erStørreEnn 0
            }

        val haddeFradragForMerkostnaderIFjor = fjoraaretsSkattemelding
