            .hvorMinstEnForekomst {
                fastsatt(inntektsaar2021.fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0
            }

        // kontrollen gir utslag dersom skattyter har svar Nei på mottatt fradrag i 2020 og/eller 2021,
        // og skattyter IKKE har oppgitt pendlerbolig med startdato før inntektsåret
        // og skattyter hadde pendleropphold og fradrag for merkostnader i fjor
        skattemelding
            .forForekomsterAv(pendleropphold)
            .utslagFor {
                val pendleroppholdFoerInntektsaaret = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst {
                        verdiFor { startdato }.dato erMindreEnn datoIInntektsaar
                    }
                hvis(
                    (mottokFradragForPendleroppholdILoepetAvToForegaaendeInntektsaar erLik false) og
                        ikke(pendleroppholdFoerInntektsaaret) og
                        haddePendleroppholdIFjor og
                        haddeFradragForMerkostnaderIFjor
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val defaultSluttPendlerboligNaarIkkeOppgitt = "2022-12-31".dato
      val defaultSluttArbeidsstedNaarIkkeOppgitt = "2022-12-31".dato

      // kontrollen gir utslag og merknad dersom skattyter ikke har arbeidssted i samme periode som oppgitt pendlerbolig
      // må også hensynta dersom skattyter ikke har oppgitt sluttdato for arbeidssted og/eller pendlerbolig
      skattemelding
      .fokusområde("Fokusområde")
        .forForekomsterAv(pendleropphold)
        .forForekomsterAv(spesifikasjonAvPendlerbolig)
        .utslagFor {

          val startPendlerbolig = verdiFor { startdato }
          val sluttPendlerbolig = verdiFor { sluttdato }

          val harIkkeArbeidsstedISammePeriode = skattemelding
          .forForekomsterAv(pendleropphold)
            .forForekomsterAv(spesifikasjonAvArbeidssted)
            .harIngenForekomsterDer {
              val startArbeidssted = verdiFor { startdato }
              val sluttArbeidssted = verdiFor { sluttdato }

              val harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoPendlerboligOgArbeidssted = ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik  defaultSluttPendlerboligNaarIkkeOppgitt)) og
              ((defaultSluttArbeidsstedNaarIkkeOppgitt erEtterEllerLik startPendlerbolig) og (defaultSluttPendlerboligNaarIkkeOppgitt erFørEllerLik  defaultSluttArbeidsstedNaarIkkeOppgitt))


              val harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoArbeidssted = ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik  sluttPendlerbolig)) og
              ((defaultSluttArbeidsstedNaarIkkeOppgitt erEtterEllerLik startPendlerbolig) og (sluttPendlerbolig erFørEllerLik  defaultSluttArbeidsstedNaarIkkeOppgitt))


              val harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoPendlerbolig = ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik  defaultSluttPendlerboligNaarIkkeOppgitt)) og
              ((sluttArbeidssted erEtterEllerLik startPendlerbolig) og (defaultSluttPendlerboligNaarIkkeOppgitt erFørEllerLik  sluttArbeidssted))

              val harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig = ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik  sluttPendlerbolig)) og
              ((sluttArbeidssted erEtterEllerLik startPendlerbolig) og (sluttPendlerbolig erFørEllerLik  sluttArbeidssted))

              ((sluttArbeidssted ikkeEr oppgitt) og (sluttPendlerbolig ikkeEr oppgitt) og harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoPendlerboligOgArbeidssted) eller
              ((sluttPendlerbolig er oppgitt) og (sluttArbeidssted ikkeEr oppgitt) og harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoArbeidssted) eller
              ((sluttPendlerbolig ikkeEr oppgitt) og (sluttArbeidssted er oppgitt) og harIkkeArbeidsstedISammePeriodeSomPendlerboligUtenSluttdatoPendlerbolig) eller
              ((startPendlerbolig er oppgitt) og (sluttArbeidssted er oppgitt) og harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig)
            }

          hvis((startdato er oppgitt) og harIkkeArbeidsstedISammePeriode) {
            betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag = skattemelding
            .forForekomsterAv(gruppe(oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag))
            .summer { antallOvernattingsdoegn }

        val antallOvernattingsdøgnPendleropphold = skattemelding
            .forForekomsterAv(sti(pendleropphold, spesifikasjonAvPendlerbolig))
            .summer { antallOvernattingsdoegn }

        //kontrollen gir utslag dersom skattyter har merkostnader for mer enn 365 overnattingsdøgn
        //Gir merknadStandard
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    sti(pendleropphold, spesifikasjonAvPendlerbolig),
                    oppholdUnderYrkesOgTjenestereise,
                    oppholdUnderKjoereoppdrag
                )
            )
            .utslagFor {
                val harAntallReiser = hvorMinstEnForekomst { antallOvernattingsdoegn er oppgitt }

                hvis(
                    harAntallReiser og
                        ((antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag + antallOvernattingsdøgnPendleropphold) erStørreEnn 365)
                )

                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val pendleroppholdene = skattemelding
            .forForekomsterAv(
                sti(
                    pendleropphold,
                    spesifikasjonAvPendlerbolig
                )
            )

        val overlappendePendleropphold = pendleroppholdene.hvor {
            val start = verdiFor { startdato }.dato
            val slutt = verdiFor { sluttdato }.dato
            val overlapperMedAndreOpphold = pendleroppholdene.filtrer {
                val start2 = verdiFor { startdato }.dato
                val slutt2 = verdiFor { sluttdato }.dato
                (
                    (start2 erStørreEnnEllerLik start) og (start2 erMindreEnn slutt)
                    ) eller (
                    (slutt2 erStørreEnn start) og (slutt2 erMindreEnnEllerLik slutt)
                    )
            }
            overlapperMedAndreOpphold.antall erStørreEnn 1
        }

        // kontrollen gir utslag dersom det er registrert flere pendleropphold på samme periode
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(pendleropphold)
            .utslagFor {
                val harPendleropphold = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst { fastsatt(startdato) er oppgitt }

                hvis(harPendleropphold og overlappendePendleropphold) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        //har godtgjørelse fra arbeidsgiver
        val godtgjoerelseFraarbeidsgiver = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                behandlingsart.finnesBlant(
                    "BOLIG_UTL_PENDLER", "DIETT_HOTELL",
                    "DIETT_LANGTR_UTL", "DIETT_HYBEL",
                    "DIETT_PENSJONAT", "FRI_KO_LO_BO_PENDLER",
                    "FRI_KOST_LOSJI", "GODTGJ_EOS_PENDLER",
                    "KOSTBESPARELSE", "LOSJI_BRAKKE",
                    "TFRI_KOST_BESOK", "TFRI_KOST_O_28DG",
                    "TFRI_KOST_O_28DG_UT", "TFRI_KOST_TJENESTE",
                    "UTGGJ_REISE_KOST_LOSJI"
                )
            }

        //kontrollen gir utslag og merknad dersom skattyter har godtgjørelser fra arbeidsgiver
        //og har merkostnad hvor antall døgn er større enn 0
        //og det ikke er oppgitt at merkostnad er dekket av arbeidsgiver
        //merknaden skal kun vises på merkostnadskortet og ikke på lønnskortene
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    pendleropphold,
                    oppholdUnderKjoereoppdrag,
                    oppholdUnderYrkesOgTjenestereise
                )
            )
            .utslagFor {
                //Har ingen merkostnader av type oppholdUnderYrkesOgTjenestereise eller oppholdUnderKjoereoppdrag som er oppgitt med lavere sats
                val merkostnaderUtenGodtgjørelseLavereEnnSats = hvorMinstEnForekomst {
                    (fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                        (fastsatt(mottarKostgodtgjoerelseLavereEnnSats) erLik false)
                }

                //har ingen merkostnad av type pendleropphold med godtgjørelse fra arbeidsgiver
                val merkostnaderUtenOppgittDekketAvArbeidsgiver = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst {
                        (fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                            (fastsatt(utbetaltKostgodtgjoerelseFraArbeidsgiver.beloepSomTall) ikkeEr oppgitt)
                    }

                hvis(
                    (merkostnaderUtenOppgittDekketAvArbeidsgiver eller merkostnaderUtenGodtgjørelseLavereEnnSats) og
                        godtgjoerelseFraarbeidsgiver
                )

                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

