sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val kontrollisteOppslag = hentKontrolliste(
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr))
            .forForekomsterAv { verdi }

        val alleVerdierFraKontrolliste = kontrollisteOppslag
            .alleVerdier

        val verdiDekningFraKontrolliste = kontrollisteOppslag
            .verdierForListe { tekstVerdier("verdi8", "verdi9", "verdi10", "verdi11", "verdi12", "verdi13") }

        //kontrollen gir utslag og merknad dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = FTL_2-9_1_ledd_a/FTL_2-9_2_ld_jfr_1a/FTL_2-7a_2_ledd_b/FTL_2-7_3_ledd_b
        //(Nav Internasjonalt har fattet et vedtak om frivillig/pliktig medlemskap i norsk folketrygd helsedel)
        //og skattyter IKKE har registrert trygdefritak for pensjonsdel på utenlandsk lønn/næringsinntekt
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, naeringsinntektMv))
            .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
            .utslagForFørste {

                val harLoenn = hvorMinstEnForekomst { arbeidsgiversNavn er oppgitt }

                val harNaering = hvorMinstEnForekomst { naeringstype er oppgitt }

                val harRegistrertTrygdefritakPensjonsdelLoenn = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                            (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                            )
                                )
                    }

                val harRegistrertTrygdefritakPensjonsdelNaeringsinntekt = hvorMinstEnForekomst {
                        (fastsatt(naeringsinntektTilordnetInnehaver.beloepSomTall) er oppgitt) og
                            (fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                            )
                                )
                    }

                val harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd = hvorMinstEnForekomst {
                        (fastsatt(underskuddTilordnetInnehaver.beloepSomTall) er oppgitt) og
                            (fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                            )
                                )
                    }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_a",
                        "FTL_2-9_2_ld_jfr_1a",
                        "FTL_2-7a_2_ledd_b",
                        "FTL_2-7_3_ledd_b"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        ((harLoenn og ikke(harRegistrertTrygdefritakPensjonsdelLoenn)) eller
                            (harNaering og ikke(harRegistrertTrygdefritakPensjonsdelNaeringsinntekt) og
                                ikke(harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd)))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

