sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val erIkkeSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .forForekomsterAv(sti(personligSkattesubjekt,saerskiltSkatteplikt))
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        // kontrollen gir utslag dersom skattyter er uten forhold sjø og sokkel og har valgt "ikke skattepliktig" på lønnsinntekt,
        // og skattyters partsnr finnes i kontrollisten - de ligger i kontrollisten hvis SP har oppdrag med "Ja" på FD (fast driftssted), er Innleie i OAR og har NT1 skjema
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

                val harOppgittSkattefriLoenn = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (
                            fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                "skattefriMedTrygdeavgiftsplikt",
                                "skattefriKildeskattPaaPensjon",
                                "skattefriUtenTrygdeavgiftsplikt",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                            )
                            ) og
                            (
                                ikke(
                                    grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                        beloepSomGruppe.trygdeEllerSkattemessigUnntak
                                    )
                                )
                                )
                    }

                val partsnrIidentifikatorIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - Ikke skattepliktig loenn med NT1 skjema",
                    listeAv(partsnr)
                )

                hvis(erIkkeSjoeEllerSokkel og harOppgittSkattefriLoenn og partsnrIidentifikatorIkontrolliste) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har nullstilt lønn med grunnlag som er merket med skattefritt unntak(et utslag pr arbeidsgiver)
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .utslagFor {
                val forekomsterSomGirUtslag = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (
                            grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                "skattefriMedTrygdeavgiftsplikt",
                                "skattefriKildeskattPaaPensjon",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                "skattefriUtenTrygdeavgiftsplikt"
                            )
                            ) og
                            (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og
                            ikke(fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0)
