        // kontrollen gir merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetPersoninntekt)
            .utslagFor {
                hvis((antallMaanederDrevetIAar erStørreEnn 12) eller (antallMaanederDrevetIAar erMindreEnn 0)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattepliktig avgir konsernbidrag og avgitt konsernbidrag overstiger samlet inntekt
        // kontrollen gir faktiskFeil

     skattemeldingUpersonlig
            .forForekomsterAv(inntektOgUnderskudd)
            .utslagFor {

              	val næringsinntektISkattemeldingUP = summer{fastsatt(naeringsinntektSomTall)}

              	val underskuddISkattemeldingUP = summer{fastsatt(inntektsfradrag.underskuddSomTall)}

                val mottattKonsernbidragISkattemeldingUP = summer{fastsatt(inntektSomGruppe.samletMottattKonsernbidrag)}

                  val grunnlagFørAvgittKonsernbidrag = næringsinntektISkattemeldingUP - underskuddISkattemeldingUP + mottattKonsernbidragISkattemeldingUP 

                hvis((fastsatt(inntektsfradrag.samletAvgittKonsernbidrag) er oppgitt) og (fastsatt(inntektsfradrag.samletAvgittKonsernbidrag) erStørreEnn grunnlagFørAvgittKonsernbidrag)       
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .hvis({ naeringsspesifikasjon.harInnhold }) {

        //kontrollen gir utslag dersom skattepliktig oppgir en annen fordelingsprosent på personinntekt enn på næringsinntekt
        //kontrollen gir validering i SME

      	val andelNæringsinntekt = naeringsspesifikasjon 
      		.forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .summer{fastsatt(andelAvFordeltSkattemessigResultatTilordnetInnehaver)}

      	val antallForekomsterAvNaeringsinntekt = naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .antall

        naeringsspesifikasjon
      		.forForekomsterAv(fordeltBeregnetPersoninntekt)
            .utslagFor {
                  val andelPersoninntekt = summer{fastsatt(andelAvPersoninntektTilordnetInnehaver)}

              	  val avviksbeløp = andelNæringsinntekt - andelPersoninntekt

                hvis((antallForekomsterAvNaeringsinntekt erLik 1) og (andelNæringsinntekt erUlik andelPersoninntekt)
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, Avviksbeloep settesTil avviksbeløp
                    )
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

    val partsnr = skattepliktUpersonligGjeldende	
      	.verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

    val selskapIkkeIStorbedrift = ansvarligEnhet
      .hvorMinstEnForekomst{erStorbedrift erLik false}

    //  Validering dersom selskap fører fremførbart underskudd som er lavere enn det som fremgår av siste skatteoppgjør for året før 
    // gir merknad i valideringstjenesten

    skattemeldingUpersonlig
        .forForekomsterAv(inntektOgUnderskudd)           
       	.utslagFor {
               	val finnesIKontrollisteFusjonFisjonInnsats = finnesIKontrolliste(
                    "kontrolliste - underskudd fusjonFisjonInnsats",
                    listeAv(partsnr)
                )
                val fremførtUnderskuddEgenfastsatt = summer{fastsatt(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAarSomTall)}
                val fremførtUnderskuddUtkast = summer{grunnlag(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAarSomTall)}                
          		val differanseFremfoerbartUnderskudd = fremførtUnderskuddUtkast - fremførtUnderskuddEgenfastsatt

                    hvis((differanseFremfoerbartUnderskudd erStørreEnn 50000) og (ikke(finnesIKontrollisteFusjonFisjonInnsats)) og 
                       selskapIkkeIStorbedrift
                       )  {
                        betjeningsstrategiSettesTil(
                            ikke,
                            merknadInformasjon,
                            1
                        )
                    }
                }
        }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

    val partsnr = skattepliktUpersonligGjeldende	
      	.verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

    val selskapIkkeIStorbedrift = ansvarligEnhet
      .hvorMinstEnForekomst{erStorbedrift erLik false}

    //  Validering dersom selskap fører fremførbart underskudd som er høyere enn det som fremgår av siste skatteoppgjør for året før 
    // gir merknad i valideringstjenesten

    skattemeldingUpersonlig
        .forForekomsterAv(inntektOgUnderskudd)           
       	.utslagFor {
               	val finnesIKontrollisteFusjonFisjonInnsats = finnesIKontrolliste(
                    "kontrolliste - underskudd fusjonFisjonInnsats",
                    listeAv(partsnr)
                )
                val fremførtUnderskuddEgenfastsatt = summer{fastsatt(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAarSomTall)}
                val fremførtUnderskuddUtkast = summer{grunnlag(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAarSomTall)}                
          		val differanseFremfoerbartUnderskudd = fremførtUnderskuddEgenfastsatt - fremførtUnderskuddUtkast

          		val harVedlegg = fastsatt(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAarSomTall) har vedlegg

                    hvis((differanseFremfoerbartUnderskudd erStørreEnn 50000) og (ikke(finnesIKontrollisteFusjonFisjonInnsats)) og (ikke(harVedlegg)))
                    {
                        betjeningsstrategiSettesTil(
                            ikke,
                            merknadInformasjon,
                            1
                        )
                    }
                }
        }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når selskapet har opplysninger om gevinst av obligasjon/sertifikat og summen av disse er høyere
        // enn beløp ført i post 8079 i resultatregnskapet
        // kontrollen gir merknad i valideringstjenesten

        val gevinstObligasjonManglerIResultat = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, finansinntekt))
            .forForekomsterAv(inntekt)
            .filtrer { fastsatt(type).finnesBlant("8079") }
            .summer { beloepSomGruppe.beloepSomTall }

        skattemeldingUpersonlig
            .forForekomsterAv(sti(spesifikasjonAvForholdRelevanteForBeskatning, obligasjonOgSertifikat))
            .utslagFor {
                val sumGevinstObligasjon = summer { gevinstVedRealisasjonAvObligasjonOgSertifikat }

                val avvikGevinst = sumGevinstObligasjon - gevinstObligasjonManglerIResultat

                hvis(gevinstObligasjonManglerIResultat erMindreEnn sumGevinstObligasjon) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil avvikGevinst
                    )
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når selskapet har opplysninger om renteinntekt av obligasjon/sertifikat og summen av disse er høyere
        // enn beløp ført i post 8090 i resultatregnskapet
        // kontrollen gir merknad i valideringstjenesten

        val renterObligasjonManglerIResultat = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, finansinntekt))
            .forForekomsterAv(inntekt)
            .filtrer { fastsatt(type).finnesBlant("8090") }
            .summer { beloepSomGruppe.beloepSomTall }

        skattemeldingUpersonlig
            .utslagFor {
                val sumRenteinntektObligasjon =
                    forForekomsterAv(sti(spesifikasjonAvForholdRelevanteForBeskatning, obligasjonOgSertifikat))
                        .summer { renteinntektAvObligasjonOgSertifikatSomTall }

                val avvikRenter = sumRenteinntektObligasjon - renterObligasjonManglerIResultat

                hvis(renterObligasjonManglerIResultat erMindreEnn sumRenteinntektObligasjon) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil avvikRenter
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {
      //kontrollen gir utslag dersom summen av alle forekomster av fordelt skattemessig resultat for næring avviker fra skattemessig resultat
      //kontrollen medfører arbeidsoppgave i SMIA

      val sumSkattemessigResultat = naeringsspesifikasjon
      .forForekomsterAv(beregnetNaeringsinntekt.skattemessigResultat)
        .summer { beloepSomTall }

      naeringsspesifikasjon
      .fokusområde("FokusFordeltBeregnetNaeringsinntekt")
        .utslagFor {
          val sumFordeltSkattemessigResultat = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .summer {fastsatt(fordeltSkattemessigResultat.beloepSomTall)}
          val differanseSkattemessigResultat = sumFordeltSkattemessigResultat - sumSkattemessigResultat

          hvis((differanseSkattemessigResultat erStørreEnn 1000) eller (differanseSkattemessigResultat erMindreEnn -1000)) {
            betjeningsstrategiSettesTil(
              ikke, faktiskFeil, 1,
              Avviksbeloep settesTil differanseSkattemessigResultat
            )
          }
        }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når selskapet har opplysninger om tap av obligasjon/sertifikat og summen av disse er høyere
        // enn beløp ført i post 8179 i resultatregnskapet
        // kontrollen gir merknad i valideringstjenesten

        val tapObligasjonManglerIResultat = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, finanskostnad))
            .forForekomsterAv(kostnad)
            .filtrer { fastsatt(type).finnesBlant("8179") }
            .summer { beloepSomGruppe.beloepSomTall }

        skattemeldingUpersonlig
            .forForekomsterAv(sti(spesifikasjonAvForholdRelevanteForBeskatning, obligasjonOgSertifikat))
            .utslagFor {
                val sumTapObligasjon = summer { tapVedRealisasjonAvObligasjonOgSertifikat }

                val avvikTap = sumTapObligasjon - tapObligasjonManglerIResultat

                hvis(tapObligasjonManglerIResultat erMindreEnn sumTapObligasjon) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil avvikTap
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når det er avvik mellom sum skattekostnad i resultatregnskapet og tilbakeført skattekostnad i permanente forskjeller
        // kontrollen medfører merknad i valideringstjenesten

        val positivSkattekostnadPermanentForskjell = naeringsspesifikasjon
            .forForekomsterAv(permanentForskjell)
            .filtrer { fastsatt(permanentForskjellstype) erLik "positivSkattekostnad" }
            .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

        val negativSkattekostnadPermanentForskjell = naeringsspesifikasjon
            .forForekomsterAv(permanentForskjell)
            .filtrer { fastsatt(permanentForskjellstype) erLik "negativSkattekostnad" }
            .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

        naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, sumSkattekostnad))
            .utslagFor {
                hvis(beloepSomTall erUlik (positivSkattekostnadPermanentForskjell - negativSkattekostnadPermanentForskjell)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når
        // kontrollen medfører merknad i valideringstjenesten
        val aarsresultatIVirksomheten = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, aarsresultat))
            .summer { beloepSomTall }

        val harFullRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst { regnskapspliktstype.finnesBlant("fullRegnskapsplikt", "begrensetRegnskapsplikt") }

        naeringsspesifikasjon
            .forForekomsterAv(egenkapitalavstemming)
            .utslagFor {
                val oppgittOverskudd = forForekomsterAv(egenkapitalendring)
                    .filtrer { fastsatt(egenkapitalendringstype) erLik "aaretsOverskudd" }
                    .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

                val oppgittUnderskudd = forForekomsterAv(egenkapitalendring)
                    .filtrer { fastsatt(egenkapitalendringstype) erLik "aaretsUnderskudd" }
                    .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

                val oppgittOverskuddEllerUnderskudd = oppgittOverskudd - oppgittUnderskudd
                val avvikIVisning = aarsresultatIVirksomheten - oppgittOverskuddEllerUnderskudd

                hvis(harFullRegnskapspliktEllerBegrensetRegnskapsplikt og (aarsresultatIVirksomheten erUlik oppgittOverskuddEllerUnderskudd)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,
                        Avviksbeloep settesTil avvikIVisning
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        // kontrollen gir utslag når personinntekt er beregnet uten at det er oppgitt beløp i feltet for skattemessig resultat tilordnet innehaver
        // kontrollen avviser innleveringen
        val harPersoninntekt = naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetPersoninntekt)
            .hvorMinstEnForekomst { aaretsBeregnedePersoninntektFoerFordelingOgSamordning.beloepSomTall erStørreEnn 0 }

        naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .utslagFor {
                hvis((harPersoninntekt) og (fordeltSkattemessigResultatEtterKorreksjon.beloepSomTall ikkeEr oppgitt)) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når det er rapportert "skattemessig resultat for næring etter korreksjon" uten at det også er rapportert "årets beregnede personinntekt"
        // kontrollen gir merknad i valideringstjenesten
        val harPersoninntekt = naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetPersoninntekt)
            .filtrer { (fastsatt(aaretsBeregnedePersoninntektFoerFordelingOgSamordning.beloepSomTall) er oppgitt) }
            .verdierFor { identifikatorForFordeltBeregnetPersoninntekt }

        val harNæringPåSvalbard = naeringsspesifikasjon
      		.forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .hvorMinstEnForekomst{fastsatt(kommunenummer) erLik "2100"}

        val harSkattepliktTilSvalbard = skattepliktGjeldende
      		.forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst{personligSkattesubjekt.skattepliktTilSvalbard er oppgitt}

        val harNæringsinntekt = naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .hvorMinstEnForekomst {
                (identifikatorForFordeltBeregnetPersoninntekt finnesIkkeBlant (harPersoninntekt)) og
              		(ikke(harNæringPåSvalbard) eller ikke(harSkattepliktTilSvalbard)) og 
                    (fordeltSkattemessigResultatEtterKorreksjon.beloepSomTall er oppgitt)  }

        naeringsspesifikasjon
            .utslagFor {
                hvis(harNæringsinntekt) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        // kontrollen gir utslag dersom skattepliktig beregner personinntekt under ett for næringstyper med ulik trygdeavgiftssats
        // kontrollen gir faktiskFeil

      naeringsspesifikasjon
            .utslagFor {
            val identifikatorHøySats = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
             	.filtrer{naeringstype.finnesBlant(
              		"jordbrukGartneriPelsdyrnaeringMv","reindrift","annenNaering","skiferproduksjon","skogbruk")}
    .verdierFor{fastsatt(identifikatorForFordeltBeregnetPersoninntekt)}

            val identifikatorMellomSatsFamiliebarnehage = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
              	.filtrer{naeringstype erLik "familiebarnehageIEgetHjem"}
              	.filtrer{fastsatt(identifikatorForFordeltBeregnetPersoninntekt).finnesBlant(identifikatorHøySats)}
    .verdierFor{fastsatt(identifikatorForFordeltBeregnetPersoninntekt)}

            val identifikatorMellomSatsFisk = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
              	.filtrer{naeringstype erLik "fiskeOgFangst"}
              	.filtrer{fastsatt(identifikatorForFordeltBeregnetPersoninntekt).finnesBlant(identifikatorHøySats)}
    .verdierFor{fastsatt(identifikatorForFordeltBeregnetPersoninntekt)}

         hvis((identifikatorMellomSatsFamiliebarnehage.antall erStørreEnn 0) eller
              (identifikatorMellomSatsFisk.antall erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig)
    {
        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val harNæringsinntekt = skattemelding 
      		.forForekomsterAv(naeringsinntektMv)
          	.hvorMinstEnForekomst{naeringsinntektTilordnetInnehaver.beloepSomTall erStørreEnn 0}   

        //kontrollen gir utslag og merknad dersom skattyter har oppgitt betalt skatt i utlandet
        //og det ikke er oppgitt noe annet kort med samme land og dobbeltbeskatningsmetode = kredit
        //skal IKKE gi utslag dersom skatteplikt er begrenset
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagFor {
                val landMedUtenlandskSkatt = verdiFor { landkode }

                val harKravOmKreditfradragForUtenlandskSkatt = hvorMinstEnForekomst {
                        (kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall erStørreEnn 0) }

                val eiendommerIUtlandetMedBetaltSkatt = skattemelding
                    .forForekomsterAv(fastEiendom)
                    .filtrer { fastsatt(landkode) erLik landMedUtenlandskSkatt }
                    .verdierFor { internEiendomsidentifikator }

                val harIngenEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemelding
                    .forForekomsterAv(
                        gruppe(
                            realisasjonAvFastEiendom,
                            sjablongbehandletUtleieIUtlandet,
                            regnskapsbehandletUtleieIUtlandet,
                            formuesgrunnlagSelveidBolig,
                            formuesgrunnlagBoenhetIBoligselskap,
                            formuesgrunnlagFlerboligbygning,
                            formuesgrunnlagTomt,
                            formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                            formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                            formuesgrunnlagBorett,
                            formuesgrunnlagEgenFritaksbehandletBolig,
                            formuesgrunnlagRegnskapsbehandletBolig,
                            formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                            formuesgrunnlagRegnskapsbehandletFritidseiendom,
                            formuesgrunnlagSelveidFritidseiendom
                        )
                    )
                    .filtrer {
                        fastsatt(internEiendomsidentifikator).finnesBlant(
                            eiendommerIUtlandetMedBetaltSkatt
                        )
                    }
                    .forAlleForekomster
                    .harIngenForekomsterDer { fastsatt(metodeVedDobbeltbeskatning) erLik "kredit" }

                val harIngenAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemelding
                    .forForekomsterAv(
                        gruppe(
                            arbeidsgiver,
                            pensjonsinntekt,
                            konto,
                            aksjeIkkeIAksjonaerregisteret,
                            aksjeIAksjonaerregisteret,
                            verdipapirfond,
                            obligasjonOgSertifikat,
                            aksjesparekonto,
                            annetFinansprodukt,
                            skattefavorisertIndividuellSparingTilPensjon,
                            annenKapitalinntekt,
                            skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler, 
                            virtuellEiendel,
                            deltakersAndelAvFormueOgInntekt
                        )
                    )
                    .filtrer { fastsatt(landkode) erLik landMedUtenlandskSkatt }
                    .forAlleForekomster
                    .harIngenForekomsterDer {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                val harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil =
                    skattemelding
                        .forForekomsterAv(grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet)
                        .harIngenForekomsterDer { fastsatt(landkode) erLik landMedUtenlandskSkatt }

                hvis(
                    ikke(harBegrensetSkatteplikt) og
                        harKravOmKreditfradragForUtenlandskSkatt og
                  		harNæringsinntekt og
                        harIngenEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngenAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1, skjulerSkatteberegning)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattepliktig krever fradrag for kostnader ved bruk av privatbil i næring i post 7080, utover det maksimale fradragsbeløpet
        // kontrollen gir merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv(resultatregnskap.driftskostnad.annenDriftskostnadSomGruppe)
            .forForekomsterAv(kostnad)
            .utslagFor {
                hvis((type erLik "7080") og (beloepSomGruppe.beloepSomTall erStørreEnn 21000)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        kortTypeReferanse settesTil "kostnaderTilKjoeretoeyBruktINaering"
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når beløp i felt "3410 Tilskudd kompensasjonsordningen" i NSP er større enn eller lik felt "Kompensasjon for tapt inntekt utbetalt fra NAV" (preutfylt) i SME
        // kontrollen gir merknad i valideringstjenesten
        val kompensasjonISkattemeldingen = skattemelding
      		.forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
            .filtrer { fastsatt(behandlingsart) erLik "KOMP_PERS_INNT" }
            .summer { beloepSomGruppe.beloepSomTall }

      	naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, driftsinntekt, annenDriftsinntekt))
            .utslagFor {
                val kompensasjonINæringsspesifikasjon = forForekomsterAv(inntekt)
            		.filtrer { fastsatt(type) erLik "3410" }
            		.summer { beloepSomGruppe.beloepSomTall }

                val avviksbeloepKompensasjon = kompensasjonINæringsspesifikasjon - kompensasjonISkattemeldingen

                hvis((kompensasjonINæringsspesifikasjon erStørreEnnEllerLik kompensasjonISkattemeldingen) og (avviksbeloepKompensasjon erStørreEnn 0)){
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                      	kortTypeReferanse settesLik "tilskudd",
                        Avviksbeloep settesTil avviksbeloepKompensasjon
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        //Kontrollen gir utslag når sum driftsinntekter er blank/null, og sum driftskostnader er lik beløp på konto 7700.
        //Skal kun slå ut når sum driftskostnader/konto 7700 er mellom kr 50 000-100 000.
        //kontrollen gir merknad i SME

        val driftsinntekterIkkeOppgitt = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, driftsinntekt))
            .harIngenForekomsterDer { sumDriftsinntekt er oppgitt }

        val driftsinntekterSummert = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, driftsinntekt, sumDriftsinntekt))
            .summer { beloepSomTall }

        val driftskostnadSummert = naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap, driftskostnad, sumDriftskostnad))
            .summer { beloepSomTall }

        naeringsspesifikasjon
            .forForekomsterAv(sti(resultatregnskap.driftskostnad.annenDriftskostnadSomGruppe))
            .utslagFor {

                val beløpPåKonto7700 = forForekomsterAv(kostnad)
                    .filtrer { fastsatt(type) erLik "7700" }
                    .summer { beloepSomGruppe.beloepSomTall }

                hvis(
                    (driftsinntekterIkkeOppgitt eller (driftsinntekterSummert erMindreEnnEllerLik 0)) og
                        (driftskostnadSummert erLik beløpPåKonto7700) og (beløpPåKonto7700 erStørreEnnEllerLik 50000) og
                        (beløpPåKonto7700 erMindreEnnEllerLik 100000)

                ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon,
                        1,
                        kortTypeReferanse settesLik "andreDriftskostnader"
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        //kontrollen gir merknad med anbefalning om å legge ved vedlegg dersom skattyter har overstyrt et eller flere felt i næringsspesifikasjonen
        //kontrollen gir ikke utslag når felter i temaet "samletGjeldOgFormuesobjekter" er overstyrt
        //kontrollen gir merknad i SME

        naeringsspesifikasjon
            .forForekomsterAv(
                gruppe(
                    spesifikasjonAvAnleggsmiddel,
                    spesifikasjonAvOmloepsmiddel,
                    beregnetNaeringsinntekt,
                    fordeltBeregnetNaeringsinntekt,
                    fordeltBeregnetPersoninntekt,
                    egenkapitalavstemming,
                    skogbruk
                )
            )
            .forAlleForekomster
            .utslagFor {

                hvis((beloepSomTall er oppgitt) og (erOverstyrt erLik true))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattepliktig ikke har oppgitt antall dager ombord fiskebåt
        // kontrollen gir merknad i SME
        val antallDagerOmbordFinnesIkke = skattemelding
            .forForekomsterAv(saerskiltFradragForFiskereOgFangstfolk)
            .hvorMinstEnForekomst { driftstidIFiskeOgFangst ikkeEr oppgitt }

        val næringsinntektErSkattefri = skattemelding
            .forForekomsterAv(grunnlagKnyttetTilLottaker)
            .hvorMinstEnForekomst {
                (naeringsinntektForLottaker er oppgitt) og
                    (fastsatt(naeringsinntektForLottaker.trygdeEllerSkattemessigUnntak).finnesBlant(
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftsplikt"
                    ))
            }

        skattemelding
            .forForekomsterAv(saerskiltFradragForFiskereOgFangstfolk)
            .utslagFor {
                val harIkkeVilkårstype = harIngenForekomsterDer{vilkaarstype er oppgitt}

              	hvis(
                    ((grunnlagForBeregningAvSaerskiltFradragForFiskereOgFangstfolk.beloepSomTall erStørreEnn 0) og
                     antallDagerOmbordFinnesIkke og harIkkeVilkårstype) og
                  	ikke(næringsinntektErSkattefri)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .hvis({ naeringsspesifikasjon.harInnhold }) {

        //kontrollen gir utslag dersom skattepliktig er ugift og rapporterer mindre enn 100 % av næringsinntekten og/eller personinntekten fra næringsspesifikasjonen
        //kontrollen gir validering i SME

        val erGift = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .forForekomsterAv(sti(personligSkattesubjekt, sambeskatningMedAnnenPart))
            .hvorMinstEnForekomst { sambeskatningsrolle erLik "ektefelle" }

        val harEktefelleSaerbo = opplysningerOmSkattesubjektDokument
            .forForekomsterAv { opplysningerOmSkattesubjekt }
            .forForekomsterAv { relasjonTilAnnenPart }
            .hvorMinstEnForekomst { fastsatt(subjektetsRelasjonTilAnnenPart) erLik "ektefelleSaerbo" }

      	val andelPersoninntektUnder100 = naeringsspesifikasjon 
      		.forForekomsterAv(fordeltBeregnetPersoninntekt)
            .hvorMinstEnForekomst{andelAvPersoninntektTilordnetInnehaver erUlik 100}

        naeringsspesifikasjon
      		.forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .utslagFor {

                hvis(ikke(erGift) og ikke(harEktefelleSaerbo) og (andelPersoninntektUnder100 eller (andelAvFordeltSkattemessigResultatTilordnetInnehaver erUlik 100))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter rapporterer personinntekt fra ektefelle, men ikke også næringsinntekt
        // kontrollen gir merknad i SME

        skattemelding
            .forForekomsterAv(naeringsinntektMvFordeltFraEktefelle)
            .utslagFor {
                hvis((personinntekt.beloepSomTall er oppgitt) og (naeringsinntektSomGruppe.beloepSomTall ikkeEr oppgitt)) {
