sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold })
    {
        val harGyldigVirksomhetstype = naeringsspesifikasjon
      		.forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst{virksomhetstype.finnesBlant(
            "enkeltpersonforetak",
            "oevrigSelskap",
            "selskapMedDeltakerfastsetting")}

      	val harIngenRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst { regnskapspliktstype erLik "ingenRegnskapsplikt" }

        val harFeilAnleggsmiddelTypeForIngenRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(sti(balanseregnskap, anleggsmiddel, balanseverdiForAnleggsmiddel))
            .forForekomsterAv(balanseverdi)
            .hvorMinstEnForekomst {
                type
                    .finnesBlant( "1350", "1360",)
            }
        val harIngenRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst { regnskapspliktstype.finnesBlant ("ingenRegnskapsplikt","begrensetRegnskapsplikt") }

        val harFeilAnleggsmiddelTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(sti(balanseregnskap, anleggsmiddel, balanseverdiForAnleggsmiddel))
            .forForekomsterAv(balanseverdi)
            .hvorMinstEnForekomst {
                type
                    .finnesBlant("1070", "1180", "1312", "1313", "1320", "1331", "1332", "1340", "1395")
            }
        val harFullRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst {regnskapspliktstype.finnesBlant ("fullRegnskapsplikt","begrensetRegnskapsplikt") }

        val harFeilAnleggsmiddelTypeForFullRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(sti(balanseregnskap, anleggsmiddel, balanseverdiForAnleggsmiddel))
            .forForekomsterAv(balanseverdi)
            .hvorMinstEnForekomst {
                type erLik "1140"
            }
      	val harFullRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst {regnskapspliktstype erLik "fullRegnskapsplikt"}

        val harFeilAnleggsmiddelTypeForFullRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv(sti(balanseregnskap, anleggsmiddel, balanseverdiForAnleggsmiddel))
            .forForekomsterAv(balanseverdi)
            .hvorMinstEnForekomst {
                type
                    .finnesBlant("1295", "1296", "1298")
            }
        // Kontrollen gir utslag dersom skattyter har regnskapsplikttype 1, 2 eller 5
        // Sammen med minst en av de nevnte typene anleggsmiddel
        naeringsspesifikasjon utslagFor hvis( harGyldigVirksomhetstype og
            (harIngenRegnskapsplikt og harFeilAnleggsmiddelTypeForIngenRegnskapsplikt) eller
                (harIngenRegnskapspliktEllerBegrensetRegnskapsplikt og harFeilAnleggsmiddelTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt) eller
                (harFullRegnskapspliktEllerBegrensetRegnskapsplikt og harFeilAnleggsmiddelTypeForFullRegnskapspliktEllerBegrensetRegnskapsplikt) eller
                (harFullRegnskapsplikt og harFeilAnleggsmiddelTypeForFullRegnskapsplikt)

        ) {
            betjeningsstrategiSettesTil(
                ikke,
                faktiskFeil,
                1,
                Gruppering settesTil Næring
            )
        }
    }

