sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når sjablongberegnet fradrag i skogbruk i fordelt beregnet næringsinntekt er ulik summen av sjablongberegnet fradrag pr tømmerkonto
        // kontrollen gir merknad i valideringstjenesten
        val sumSjablongberegnetFradragISkogbruk = naeringsspesifikasjon
            .forForekomsterAv(sti(skogbruk, skogOgToemmerkonto))
            .summer { inntektOgKostnad.sjablongberegnetFradragKnyttetTilBiomasseOgVedproduksjon.beloepSomTall }

        naeringsspesifikasjon
            .forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .utslagFor {
                val næringstypeSkogbruk = hvorMinstEnForekomst { naeringstype erLik "skogbruk" }

                val differanseSjablongberegnetFradrag =
                    sjablongberegnetFradragFraBiomasseVedproduksjon.beloepSomTall - sumSjablongberegnetFradragISkogbruk
                // må bruke pluss i beregningen siden sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall angis med minus i fordeltBeregnetNaeringsinntekt

                hvis(næringstypeSkogbruk og (sjablongberegnetFradragFraBiomasseVedproduksjon.beloepSomTall erUlik (sumSjablongberegnetFradragISkogbruk))) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil differanseSjablongberegnetFradrag
