                val sjablongISkogbruk = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
                    .hvorMinstEnForekomst { ((naeringstype erLik "skogbruk") og (sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall er oppgitt)) }

                val sjablongIJordbruk = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
                    .hvorMinstEnForekomst { ((naeringstype erLik "jordbrukGartneriPelsdyrnaeringMv") og (sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall er oppgitt)) }

                val sumSjablongISkogOgJord = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
                    .summer { sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall }

                hvis(sjablongISkogbruk og sjablongIJordbruk og (sumSjablongISkogOgJord erUlik 0))

                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
