        		.hvorMinstEnForekomst {grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt}

       	val fastsattSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

        val grunnlagSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

          hvis(harSykepengerFisker og ((fastsattSykepengerFisker - grunnlagSykepengerFisker) erUlik 0))
          {
            betjeningsstrategiSettesTil(
              ikke,
              merknadStandard,
