sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag når det er rapportert driftskostnader på skog- og tømmerkonto, men driftskostnadene er ikke rapportert i resultatregnskapet. 
        // kontrollen gir merknad i valideringstjenesten

        naeringsspesifikasjon
                .utslagFor {
                val sumInntektsfradragFraTømmerkonto = forForekomsterAv(sti(skogbruk,skogOgToemmerkonto))
               		.forForekomsterAv(sti(inntektOgKostnad,driftskostnader))
                    .summer {beloepSomTall }

                val harSkog = forForekomsterAv(sti(skogbruk,skogOgToemmerkonto))
                	.forForekomsterAv(sti(inntektOgKostnad,driftskostnader))
                    .hvorMinstEnForekomst{beloepSomTall er oppgitt}

              	val beløpOverførtTilTømmerkonto = forForekomsterAv(sti(resultatregnskap, driftskostnad, annenDriftskostnadSomGruppe))
                    .forForekomsterAv(kostnad)
                    .filtrer { type erLik "7910" }
                    .summer { beloepSomGruppe.beloepSomTall }

                    val sumDriftskostnaderIRegnskapet = forForekomsterAv(sti(resultatregnskap, driftskostnad, sumDriftskostnad))
                    .summer {beloepSomTall }

                  hvis(harSkog og (sumInntektsfradragFraTømmerkonto erStørreEnn (sumDriftskostnaderIRegnskapet - beløpOverførtTilTømmerkonto))
                          ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1 )
                }
            }
    }

sakstype(Personlig)
            .kontekst(ValideringSkattepliktig) {

        // Merknad til skattepliktige når salgsinntekter i skog og tømmer er høyere enn salgsinntekter i resultatregnskapet
        // kontrollen medfører merknad i SME

         val harSalgsinntekUnderSkogbruk = naeringsspesifikasjon
         	.forForekomsterAv(sti(skogbruk, skogOgToemmerkonto))
            .summer{(fastsatt(inntektOgKostnad.salgsinntektOgUttakFraSkurtoemmerMassevirkeRotsalgMv.beloepSomTall)) +
            		(fastsatt(inntektOgKostnad.salgsinntektOgUttakAvVedOgBiomasse.beloepSomTall))  +
            		(fastsatt(inntektOgKostnad.andreDriftsinntekter.beloepSomTall))}

        val harIkkeSalgsinntekt = naeringsspesifikasjon
           	.forForekomsterAv(sti(resultatregnskap, driftsinntekt))
            .harIngenForekomsterDer{salgsinntekt er oppgitt}

    naeringsspesifikasjon
      	.utslagFor {
          	  val harIkkeSalgsinntekt = forForekomsterAv(sti(resultatregnskap, driftsinntekt,salgsinntekt))
                  .harIngenForekomsterDer{inntekt er oppgitt}

              val harIkkeAnnenDriftsinnekt = forForekomsterAv(sti(resultatregnskap, driftsinntekt, annenDriftsinntekt))
    			 .forForekomsterAv(inntekt)
                 .harIngenForekomsterDer {fastsatt(type) erLik "3900" }

              val salgsinntekterIResultat = forForekomsterAv(sti(resultatregnskap, driftsinntekt, salgsinntekt))
    			 .forForekomsterAv(inntekt)
                 .filtrer {fastsatt(type).finnesBlant("3000","3100","3200") }
                 .summer {beloepSomGruppe.beloepSomTall }

              val andreDriftsinntekterIResultat = forForekomsterAv(sti(resultatregnskap, driftsinntekt, annenDriftsinntekt))
    			 .forForekomsterAv(inntekt)
                 .filtrer {fastsatt(type) erLik "3900" }
                 .summer {beloepSomGruppe.beloepSomTall }

              val sumInntekterIResultat = salgsinntekterIResultat + andreDriftsinntekterIResultat

          	  val avvikSkogbrukOgResultat = harSalgsinntekUnderSkogbruk - sumInntekterIResultat

              	hvis(((harSalgsinntekUnderSkogbruk erStørreEnn 0) og (avvikSkogbrukOgResultat erStørreEnn 1000)) eller
                     (harIkkeSalgsinntekt og harIkkeAnnenDriftsinnekt og (harSalgsinntekUnderSkogbruk erStørreEnn 0))
              	){
                	betjeningsstrategiSettesTil(
                     	ikke, merknadTips, Avviksbeloep settesTil avvikSkogbrukOgResultat,
                      kortTypeReferanse settesLik "salgsinntekt"
                            )
                        }
                    }
            }

