    val harInntektFradragFraTømmerkonto = naeringsspesifikasjon
     	.forForekomsterAv(sti(skogbruk, skogOgToemmerkonto))
        .summer{(fastsatt(inntektFraToemmerkonto.beloepSomTall)) +
        		(fastsatt(inntektsfradragFraToemmerkonto.beloepSomTall))}

    val harIkkeInntektFradragPåTømmerkonto = naeringsspesifikasjon
       	.forForekomsterAv(sti(skogbruk, skogOgToemmerkonto))
        .summer{(fastsatt(driftsresultatSomKanOverfoeresTilToemmerkonto.beloepSomTall)) }

    naeringsspesifikasjon
        .fokusområde("FokusFordeltBeregnetNaeringsinntekt")
        .utslagFor {
          val harIkkeNæringstypeSkogbruk = forForekomsterAv(fordeltBeregnetNaeringsinntekt)
          	.harIngenForekomsterDer {(naeringstype erLik "skogbruk")}

          	hvis(((harInntektFradragFraTømmerkonto erUlik 0) eller (harIkkeInntektFradragPåTømmerkonto erUlik 0)) og harIkkeNæringstypeSkogbruk)
          	{
            	betjeningsstrategiSettesTil(
                 	ikke, merknadStandard
                        )
                    }
                }
        }

// Kontrollen gir utslag når selskap rapporterer ufullstendige opplysninger knyttet til skatte- og avgiftsordrninger. Alle opplysningene må fylles ut.
    // KOntrollen gir merknad i valideringstjenesten.
    sakstype(Upersonlig)
        .kontekst(ValideringSkattepliktig) {

            val gyldigeStøtteordninger = skatteOgAvgiftsordningOmfattetAvOffentligStoette_2022
                .forForekomsterAv { kode }
                .verdierFor { tekniskNavn }

            val harStøtteordning = skattemeldingUpersonlig
                .forForekomsterAv(
                    sti(
                        spesifikasjonAvForholdRelevanteForBeskatning,
                        tilleggsinformasjonKnyttetTilMottakAvSkatteOgAvgiftsordningOmfattetAvOffentligStoette,
                        skatteOgAvgiftsordningOmfattetAvOffentligStoette
                    )
                )
                .hvorMinstEnForekomst {
                    (fastsatt(skatteOgAvgiftsordning) er oppgitt) og
                        (fastsatt(skatteOgAvgiftsordning).finnesBlant(gyldigeStøtteordninger))
                }

            skattemeldingUpersonlig
                .forForekomsterAv(
                    sti(
                        spesifikasjonAvForholdRelevanteForBeskatning,
                        tilleggsinformasjonKnyttetTilMottakAvSkatteOgAvgiftsordningOmfattetAvOffentligStoette
                    )
                )
                .utslagFor {
                    hvis(
                        harStøtteordning og (varIOekonomiskeVanskerVedSistAvlagtRegnskapsaar ikkeEr oppgitt) eller
                            (erIKonsern ikkeEr oppgitt) eller
                            (harUtestaaendeBetalingskravKnyttetTilUlovligUtdeltStatsstoette ikkeEr oppgitt) eller
                            (erLitenEllerMellomstorBedrift ikkeEr oppgitt) eller
                            (harMottattAnnenOffentligStoette ikkeEr oppgitt)
