sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = skattemelding
            .forForekomsterAv(sti(obligasjonOgSertifikat, aarsakTilEndring))
            .harIngenForekomsterDer { aarsak erLik "overfortTilFraEktefelle" }

        // Kontrollen slår ut hvis skattepliktige egenfastsetter tap over kr 200 000 på nytt obligasjonskort,
        // og har ikke oppgitt at det er overførst fra ektefelle/samboer
        // kontrollen gir merknad i SME

        skattemelding
            .forForekomsterAv(obligasjonOgSertifikat)
            .utslagFor {
                val harDifferanseTap = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnn 200_000

                            )
                }

         hvis(ikke(harInnsending) og oppgittEndringsaarsakIkkeErOverførtFraEktefelle og
               harDifferanseTap)
              {

                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

         val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = skattemelding
            .forForekomsterAv(sti(obligasjonOgSertifikat, aarsakTilEndring))
            .harIngenForekomsterDer { aarsak erLik "overfortTilFraEktefelle" }

        // kontrollen gir utslag når skattepliktige reduserer forhåndsutfylt renteinntekt, endring over kr 30 000,
        //  og har ikke oppgitt at det er overførst fra ektefelle/samboer
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(obligasjonOgSertifikat)
            .filtrer {
                (grunnlag(renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall) er oppgitt) 
            }
            .utslagFor {
                val differanseRenteinntektObligasjon =
                    grunnlag(renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall) - fastsatt(
                            renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall
                        )

            hvis( ikke(harInnsending) og oppgittEndringsaarsakIkkeErOverførtFraEktefelle og
                     (differanseRenteinntektObligasjon erStørreEnn 30_000)
              ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har egenfastsatt skattepliktig tap på obligasjoner
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(obligasjonOgSertifikat)
            .utslagFor {
                val harDifferanseTap = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            ) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erMindreEnn 5_000_000
                            )
                }

                val harVedlegg =
                    forForekomsterAv(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.beloepUtenHensynTilValgtPrioritertFradragstype)
                        .hvorMinstEnForekomst { beloepIValuta har vedlegg }

                // skal kun gi oppgave i SMIA fra 50 000 til 5 mill (for å unngå flere oppgaver på samme beløp)
                hvis(ikke(harInnsending) og ikke(harVedlegg) og harDifferanseTap) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert skattepliktig gevinst på obligasjoner
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(obligasjonOgSertifikat)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjonAvObligasjonOgSertifikat.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvObligasjonOgSertifikat.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvObligasjonOgSertifikat.beloepSomTall
                        )
                        )
                val harVedlegg =
                    skattepliktigGevinstVedRealisasjonAvObligasjonOgSertifikat.beloepIValuta har vedlegg

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(
                    ikke(harInnsending) og
                        ikke(harVedlegg) og
                        ((differanseGevinst erStørreEnnEllerLik 50_000) og (differanseGevinst erMindreEnn 5_000_000))
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
    }

