sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

         // Skattepliktig har redusert beløp i preutfylt pensjonskort med utbetaler NAV 
         // og skattepliktig har lagt til felt "opptjeningsland" Thailand på et pensjonskort                              
         // skal ikke gi utslag hvis kort for avtalebosted under tema Andre forhold er lagt til 
         // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
        skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { fastsatt(landkode) erLik "TH" } 
            .utslagFor {
                val sumPensjonPerUtbetaler = forForekomsterAv(perMaaned)
                    .summer { pensjonsinntektSomGruppe.beloepSomTall }

                val pensjonGrunnlagMinusFastsatt =
                    grunnlag(sumPensjonPerUtbetaler) - fastsatt(sumPensjonPerUtbetaler)

                val bosattland = 
                  skattemelding
                    .forForekomsterAv (tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
                    .hvorMinstEnForekomst { (bosattILandEtterSkatteavtale er oppgitt)
                     }

                hvis(
                 ikke(harInnsending) og ikke(harBegrensetSkatteplikt) og ikke(bosattland) og
                    (pensjonGrunnlagMinusFastsatt erStørreEnn 0)
                    )
                 {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

