        // uten å ha oppgitt lønn eller næringsspesifikasjon eller opplysninger om deltakers andel i SDF
        val harOppgittNaeringsspesifikasjon = naeringsspesifikasjon
            .hvorMinstEnForekomst { inntektsaar2022.naeringsspesifikasjon.partsreferanse er oppgitt }

        val harIKkeOppgittNaeringsInfoISkattemeldingen = skattemelding
            .harIngenForekomsterDer { fastsatt(naeringsinntektMv) er oppgitt }

        val harIkkeOppgittDeltakerinformasjonSDF = skattemelding
            .harIngenForekomsterDer { fastsatt(deltakersAndelAvFormueOgInntekt) er oppgitt }

        val harIkkeOppgittLoenn = skattemelding
            .harIngenForekomsterDer { fastsatt(arbeidsgiver) er oppgitt }

        skattemelding
            .forForekomsterAv(reiserIArbeidsforhold)
            .forForekomsterAv(
                gruppe(
                    arbeidsreise,
                    besoeksreiseTilHjemInnenforEOeS,
                    besoeksreiseTilHjemUtenforEOeS
                )
            )
            .utslagFor {
                hvis(
                    (antallReiser erStørreEnn 0) og
                        (ikke(harOppgittNaeringsspesifikasjon) og harIKkeOppgittNaeringsInfoISkattemeldingen og harIkkeOppgittDeltakerinformasjonSDF og harIkkeOppgittLoenn)
