                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val erSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst {
                type.finnesBlant(
                    "skattepliktSomSjoemann",
                    "skattepliktEtterPetroleumsskatteloven"
                )
            }

        //kontrollen gir utslag og merknad dersom skattyter har innrapporteringer med utenlandsk lønn som har trygde- eller skattemessig unntak
        //kontrollen skal kun gi utslag på skattytere som er sjø/sokkel i skatteplikt
        skattemelding

            .utslagForFørste {
                val sumUtenlandskLoennMedUnntak = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer {
                        (
                            grunnlag(behandlingsart).finnesBlant(
                                "LONN_UTL",
                                "NETTOLONN_UTL",
                                "NATURALYTELSE_UTL",
                                "BONUS_FERIEP"
                            )
                            ) og
                            (grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) er oppgitt)
                    }
                    .summer { beloepSomGruppe.beloepSomTall }

                hvis(erSjoeEllerSokkel og (sumUtenlandskLoennMedUnntak erStørreEnn 0))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

