                        (verdiFor { sluttdatoForArbeidsoppholdIUtland }.dato erMindreEnnEllerLik datoPeriodeSlutt)
                }

                val harVedlegg = sluttdatoForArbeidsoppholdIUtland har vedlegg

                hvis(harKravOmEttaarsregelenMEDSLuttdatoIInntektsaaret og ikke(harVedlegg)) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1)
                }
