            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst { type erLik "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet" }

        // kontrollen gir utslag dersom skattyter har særskilt skatteplikt på lønn fra den norske stat hele eller deler av året
        // og har en eller annen form for gjeldsrente eller gjeld
        skattemelding
            .utslagFor {
                val harGjeldEllerGjeldsrenter = forForekomsterAv(
                    gruppe(
                        annenGjeld,
                        annenKapitalkostnad,
                        annetFinansprodukt,
                        kapitalisertFesteavgift,
                        konto,
                        skyldigRestskatt
                    )
                )
                    .hvorMinstEnForekomst {
                        (
                            (paaloepteRenter.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                                (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
