                                    (paaloepteRenterFraInkassoselskap.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
                                ) eller
                            (
                                (betalteForsinkelsesrenter.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                                    (betalteForsinkelsesrenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
                                ) eller
                            ((gjeldsbeloep.beloepSomTall erStørreEnn 0) og (gjeldsbeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                            ((gjeldPaaAnnetFinansprodukt.beloepSomTall erStørreEnn 0) og (gjeldPaaAnnetFinansprodukt.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                            ((gjeldsfradragForKapitalisertFesteavgift.beloepSomTall erStørreEnn 0) og (gjeldsfradragForKapitalisertFesteavgift.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                            ((gjeldSomGruppe.beloepSomTall erStørreEnn 0) og (gjeldSomGruppe.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                            ((beloepSomGruppe.beloepSomTall erStørreEnn 0) og (beloepSomGruppe.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))
                    }

                hvis(harSkattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet og harGjeldEllerGjeldsrenter) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
