sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harForekomstMedKommunenrSvalbard = skattemelding
            .forAlleForekomster
            .hvorMinstEnForekomst { fastsatt(kommunenummer) erLik "2100" }

        val harForekomsterMedLandkodeSvalbard = skattemelding
            .forAlleForekomster
            .hvorMinstEnForekomst { fastsatt(landkode) erLik "SJ" }

        //kontrollen gir utslag dersom skatepliktig i gjeldende skatteplikt ikke har skatteplikt til Svalbard (hverken ordinær eller dødsbo)
        //og skattepliktig registrerer opplysninger som gjelder Svalbard
        skattepliktGjeldende
            .utslagFor {
                val harIkkeSkattepliktTilSvalbard = forForekomsterAv(skatteplikt)
                    .hvorMinstEnForekomst {
                        ((personligSkattesubjekt.skattepliktTilSvalbard erLik "ingenSkatteplikt") eller (personligSkattesubjekt.skattepliktTilSvalbard ikkeEr oppgitt)) og
                            ((doedsbo.skattepliktTilSvalbard erLik "ingenSkatteplikt") eller (doedsbo.skattepliktTilSvalbard ikkeEr oppgitt))
                    }

                hvis(
                    (harForekomstMedKommunenrSvalbard eller harForekomsterMedLandkodeSvalbard) og
                        harIkkeSkattepliktTilSvalbard
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1,
                    )
                }
            }
    }

