            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { (personligSkattesubjekt.skattepliktTilSvalbard erLik "global") eller (doedsbo.skattepliktTilSvalbard erLik "global") }

        val primaerboligSelveidEllerBoligselskap = skattemelding
            .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { internEiendomsidentifikator }

        val primaerboligFlerboligbygg = skattemelding
            .forForekomsterAv(sti(formuesgrunnlagFlerboligbygning, useksjonertBoenhet))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { internEiendomsidentifikator }

        // Kontrollen gir utslag dersom skattyter har globalskatteplikt på Svalbard
        // og har primærbolig i Norge, men ikke oppgitt utleie
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {
                val harprimærboligINorge = forForekomsterAv(fastEiendom)
                    .filtrer { (fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt) }
                    .hvorMinstEnForekomst {
                        (internEiendomsidentifikator.finnesBlant(primaerboligSelveidEllerBoligselskap)) eller
                            (internEiendomsidentifikator.finnesBlant(primaerboligFlerboligbygg))
                    }

                hvis(
                    harSkattepliktTilSvalbard og
                        harprimærboligINorge og
                        ikke(
                            gruppe(
                                sjablongbehandletUtleie,
                                regnskapsbehandletUtleie,
                                sjablongOgRegnskapsbehandletUtleie
                            ) er oppgitt
                        )
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadTips, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag og merknad dersom skattyter har lagt innskattefritt tilskudd eller stønad, men ikke har lagt inn noen kostnader
        // Vil da ikke få fradraget
        skattemelding
            .forForekomsterAv(saerfradragForStoreSykdomskostnader)
            .utslagFor {
                hvis((offentligSkattefriTilskuddEllerStoenad.beloepSomTall erStørreEnn 0) og (sumSykdomskostnad.beloepSomTall erLik 0)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtslagPaaKontroll = utslagPåKontroll("SÆRFRADRAG_SYKDOMSUTGIFTER_SPESIFIKASJON_HØYT")

        // kontrollen gir utslag og merknad dersom skattyter krever særfradrag for store sykdomsutgifter
        // og har krevd mer enn 30 000 i felt for "Andre merkostnader på grunn av sykdom eller varig svakhet"
        // skal ikke gi utslag dersom skattyter har lagt ved dokumentasjon
        // skal ikke få merknaden dersom skattyter har utslag på SÆRFRADRAG_SYKDOMSUTGIFTER_SPESIFIKASJON_HØYT
        skattemelding
            .forForekomsterAv(saerfradragForStoreSykdomskostnader)
            .utslagFor {
                val harKrevdFradragForsykdomsutgifterIAar = hvorMinstEnForekomst{ fastsatt(rettTilAaKreveSaerfradrag) erLik true}

                val harVedleggSykdomsutgifter = hvorMinstEnForekomst{ fastsatt(rettTilAaKreveSaerfradrag) har vedlegg }

                val sumOektForbruk = forForekomsterAv(sykdomskostnad)
                   .filtrer{fastsatt(type) erLik "annet"}
                   .summer{fastsatt(beloepSomGruppe.beloepSomTall)}

                hvis(ikke(harUtslagPaaKontroll) og
                     ikke(harVedleggSykdomsutgifter) og
                     harKrevdFradragForsykdomsutgifterIAar og
                     (sumOektForbruk erStørreEnn 30_000)){
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val saerfradragStoreSykdomsutgifterMyndighetsfastsattIFjor = fjoraaretsSkattemelding
           .forForekomsterAv(inntektsaar2021.saerfradragForStoreSykdomskostnader)
           .hvorMinstEnForekomst{fastsatt(inntektsaar2021.fradragsberettigetBeloep.beloepSomTall) er myndighetsfastsatt}

        // kontrollen gir utslag og merknad dersom skattyter fikk endret særfradrag for store sykdomsutgifter ved myndighetsfastsetting i fjor
        // og krever også særfradrag i år
        // eller skattepliktig krever særfradrag for store sykdomsutgifter med over kr 50 000 (før reduksjon til 67%)
        skattemelding
            .forForekomsterAv(saerfradragForStoreSykdomskostnader)
            .utslagFor {
                val harKrevdFradragForsykdomsutgifterIAar = hvorMinstEnForekomst{fastsatt(rettTilAaKreveSaerfradrag) erLik true}

                val harVedleggSykdomsutgifter = hvorMinstEnForekomst{fastsatt(rettTilAaKreveSaerfradrag) har vedlegg }

                val sumKravOmSaerfradrag = summer{fastsatt(sumSykdomskostnad.beloepSomTall)}

                hvis(ikke(harVedleggSykdomsutgifter) og
                    ((saerfradragStoreSykdomsutgifterMyndighetsfastsattIFjor og harKrevdFradragForsykdomsutgifterIAar) eller
                     (sumKravOmSaerfradrag erStørreEnn 50_000))) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, skjulerSkatteberegning
                    )
                }
            }
    }

