sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Kontrollen gir utslag dersom skattyter oppgir en næringseiendomstype som ikke er gyldig
        // Kontrollen medfører at innleveringen avvises
        skattemelding
            .forForekomsterAv(formuesgrunnlagIkkeUtleidNaeringseiendomINorge)
            .utslagFor {

                val harIkkeUtleidNæringseiendomINorge = hvorMinstEnForekomst {
                  (naeringseiendomstype er oppgitt) og 
                    (naeringseiendomstype.finnesIkkeBlant(
                        "industri",
                        "verksted",
                        "lager",
                        "hotell",
                        "butikkEllerKjoepesenter",
                        "kontor",
                        "tomtGrunnarealHovedfunksjon",
                        "annet"
                    ))
                }

                hvis(harIkkeUtleidNæringseiendomINorge) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        1
                    )
                }
            }
    }

sakstype(SDF)
    .kontekst(ValideringSkattepliktig) {

        val harSelskapsmelding = selskapsmeldingSdf.harInnhold

        // kontrollen gir utslag dersom upersonlig skattyter forsøker å levere med næringsspesifikasjon uten selskapsmelding      
        naeringsspesifikasjon
            .utslagFor {
                 val harNæringsspesifikasjon = forForekomsterAv(virksomhet)
                    .hvorMinstEnForekomst { virksomhetstype er oppgitt }

                hvis(harNæringsspesifikasjon og ikke(harSelskapsmelding)) 
              {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        val harSkattemelding = skattemeldingUpersonlig
           .hvorMinstEnForekomst { skattepliktSomGruppe.partsnummerSomTekst er oppgitt }

        // kontrollen gir utslag dersom upersonlig skattyter forsøker å levere med næringsspesifikasjon uten skattemelding      
        naeringsspesifikasjon
            .utslagFor {
                val harNæringsspesifikasjon = forForekomsterAv(virksomhet)
                    .hvorMinstEnForekomst { virksomhetstype er oppgitt }

                hvis(harNæringsspesifikasjon og ikke(harSkattemelding)) 
              {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
    }

sakstype(SDF)
    .kontekst(ValideringSkattepliktig) {

        val harNæringsspesifikasjon = naeringsspesifikasjon
              .forForekomsterAv(virksomhet)
              .hvorMinstEnForekomst { virksomhetstype er oppgitt }

        // kontrollen gir utslag dersom upersonlig skattyter forsøker å levere med selskapsoppgave uten næringsspesifikasjon      
        selskapsmeldingSdf
            .utslagFor {
                val harSelskapsmelding = hvorMinstEnForekomst { skatteplikt.partsnummer er oppgitt }

                hvis(harSelskapsmelding og ikke(harNæringsspesifikasjon)) 
              {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        val harNæringsspesifikasjon = naeringsspesifikasjon
              .forForekomsterAv(virksomhet)
              .hvorMinstEnForekomst { virksomhetstype er oppgitt }

        // kontrollen gir utslag dersom upersonlig skattyter forsøker å levere med skattemelding uten næringsspesifikasjon      
        skattemeldingUpersonlig
            .utslagFor {
                val harSkattemelding = hvorMinstEnForekomst { skattepliktSomGruppe.partsnummerSomTekst er oppgitt }

                hvis(harSkattemelding og ikke(harNæringsspesifikasjon)) 
