                        ((fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                            skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall
                        )) erStørreEnnEllerLik 50_000) og
                        ((fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                            skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall
                        )) erMindreEnn 20_000_000)
                }

                val harVedlegg =
                    forForekomsterAv(skattepliktigTapVedRealisasjonAvAksje.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                hvis(ikke(harVedlegg) og harDifferanseTap)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1,
                    )
                }
            }
    }

