           .forForekomsterAv { utvidetveiledning }
           .forForekomsterAv { metadata }
           .hvorMinstEnForekomst {type erLik "UTLEIE_SEKUNDAERBOLIGMODELL" }        

         val harUtleidFjoraaret = fjoraaretsSkattemelding
             .forForekomsterAv(
                 gruppe(
                     inntektsaar2021.sjablongbehandletUtleie,
                     inntektsaar2021.regnskapsbehandletUtleie,
                     inntektsaar2021.sjablongOgRegnskapsbehandletUtleie))
             .hvorMinstEnForekomst {
                 ((fastsatt(inntektsaar2021.bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall) erStørreEnn 0) eller 
                  (fastsatt(inntektsaar2021.bruttoLeieinntektVedRegnskapsbehandletUtleie.beloepSomTall) erStørreEnn 0)) }


        // Kontrollen gir utslag dersom skattyter IKKE hadde utleie i fjor, og ikke har oppgitt noen utleie i år,
        // Skal gi utslag hvis det er produsert UV av type "Utleie sekundærbolig på "skattyter 
        // AB-test: skattytere som har tallet 0,2,4,6 eller 8 i 7.siffer i partsnummeret skal få Utvidet veiledning
         skattemelding
            .utslagFor{

              val harUtleie = forForekomsterAv(gruppe(sjablongbehandletUtleie,
