kontrollformel:
sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        // kontrollen gir utslag dersom skattyter ikke har kort med lønn fra utlandet
        // og ligger i kontrolliste
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harIngenForekomsterAvUtenlandskLoenn = forForekomsterAv(arbeidsgiver)
                .harIngenForekomsterDer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }

            val utenlandskLoennIkontrolliste = finnesIKontrolliste(
                "kontrolliste - gld med loenn i Utlandet",
                listeAv(partsnr)
            )

            hvis(harIngenForekomsterAvUtenlandskLoenn og utenlandskLoennIkontrolliste) {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtvidetVeiledningPaaAKU = utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .forForekomsterAv { metadata }
            .hvorMinstEnForekomst { type.finnesBlant("AKU_KJENT_BELOEP", "AKU_UKJENT_BELOEP") }

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        // Kontrollen gir utslag dersom skattyter ligger i listen over skattytere med utenlandsk pensjon - AKU
        // og ikke har UV for AKU
        // og ikke har oppgitt pensjon i utlandet
        // kontrollen gir merknad i SME

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val partFinnesIkontrolliste = finnesIKontrolliste(
                "kontrolliste - Utenlandsk pensjon AKU", listeAv(partsnr)
            )

            val harPensjonFraUtlandet = forForekomsterAv(pensjonsinntekt)
                .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }

            hvis(
                partFinnesIkontrolliste og
                    ikke(harUtvidetVeiledningPaaAKU) og
                    ikke(harPensjonFraUtlandet)
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert annen skattepliktig kapitalinntekt på annet finansprodukt
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .filtrer { grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) - fastsatt(
                            annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall
                        )
                        )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har egenfastsatt tap på annet finansprodukt
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .forForekomsterAv(
                sti(
                    skattepliktigTapVedRealisasjonAvAnnetFinansprodukt,
                    beloepUtenHensynTilValgtPrioritertFradragstype
                )
            )
            .filtrer { grunnlag(beloepSomTall) erLik 0 }
            .utslagFor {
               val differanseTap = (fastsatt(beloepSomTall) - grunnlag(beloepSomTall))

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(ikke(harInnsending) og (differanseTap erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard 
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret formue på annet finansprodukt med 10 mill eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .utslagFor {
                val differanseGevinst = differanse(
                    grunnlag(formuesverdiForAnnetFinansprodukt.beloepSomTall),
                    fastsatt(formuesverdiForAnnetFinansprodukt.beloepSomTall)
                )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 10_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret gevinst på annet finansprodukt med 2 mill eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .utslagFor {
                val differanseGevinst =
                    differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall)
                    )

                hvis(ikke(harInnsending) og (differanseGevinst erStørreEnnEllerLik 2_000_000)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret tap på annet finansprodukt med 50 000 eller mer
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(annetFinansprodukt)
            .forForekomsterAv(sti(skattepliktigTapVedRealisasjonAvAnnetFinansprodukt, fradragsberettigetBeloep))
            .utslagFor {
                val differanseTap = differanse(grunnlag(beloepSomTall), fastsatt(beloepSomTall))

                hvis(ikke(harInnsending) og (differanseTap erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når skattepliktige egenfastsetter underkort "Virtuell valuta og kryptovaluta" uten beløp i noen av feltene, men med vedlegg
        // eller hvis den skattepliktige egenfastsetter kort uten beløp og uten vedlegg når der er lagt inn 
        //        antall i felt "Antall per 31. desember i inntektsåret" 
        //        og/eller felt "navn på virtuelle eiendeler/ kryptovaluta"
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(virtuellEiendel)
            .utslagFor {
                val harVedlegg = hvorMinstEnForekomst { gjelderFlereVirtuelleEiendeler har vedleggPåFelt }

                val ikkeHarBeloep = forAlleForekomster
                    .hvorMinstEnForekomst { (beloepSomTall erUlik 0) eller (beloepIValuta erUlik 0) }
                    .ikke()

                val harAngittAntallEllerNavn = hvorMinstEnForekomst { (antall erStørreEnn 0) eller (navn er oppgitt) }    


                hvis((ikkeHarBeloep og harVedlegg) eller
                     (ikkeHarBeloep og ikke(harVedlegg) og harAngittAntallEllerNavn)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harFordringFjoraaret = fjoraaretsSkattemelding
            .forForekomsterAv(inntektsaar2022.annenFordring)
            .hvorMinstEnForekomst {
                    (fastsatt(inntektsaar2022.opptjenteRenter.beloepSomTall) erStørreEnn 0) eller 
                    (fastsatt(inntektsaar2022.formuesverdi.beloepSomTall) erStørreEnn 0)
            }

        // kontrollen gir utslag dersom skattyter hadde fordring i fjor, men ikke har oppgitt noen fordring i år
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

            val harFordringIAar = forForekomsterAv{annenFordring}
                .hvorMinstEnForekomst {
                    (fastsatt(opptjenteRenter.beloep) erStørreEnn 0) eller
                    (fastsatt(formuesverdi.beloep) erStørreEnn 0)
                }

            hvis(ikke(harFordringIAar) og
                 harFordringFjoraaret)
            {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
        }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val loennUtenDobbeltbeskatningsmetode = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(arbeidsgiver, samledeYtelserFraArbeidsgiverPerBehandlingsart))
            .harIngenForekomsterDer { fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) erLik "denAlternativeFordelingsmetoden" }
        }

        val pensjonUtenDobbeltbeskatningsmetode = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(pensjonsinntekt, perMaaned))
            .harIngenForekomsterDer { fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) erLik "denAlternativeFordelingsmetoden" }
        }

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for den alternative fordelingsmetoden
        // og IKKE har oppgitt metode for dobbelbeskatning = den alternative fordelingsmetoden på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden)
            .utslagFor {

                val harKravOmDenAlternativeFordelingsmetoden = hvorMinstEnForekomst {
                    (startdatoForOppholdIUtland er oppgitt) eller
                        (sluttdatoForOppholdIUtland er oppgitt)
                }

                hvis(
                    harKravOmDenAlternativeFordelingsmetoden og
                        loennUtenDobbeltbeskatningsmetode og pensjonUtenDobbeltbeskatningsmetode
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val loennUtenDobbeltbeskatningsmetode = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(arbeidsgiver, samledeYtelserFraArbeidsgiverPerBehandlingsart))
            .harIngenForekomsterDer { fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) erLik "ettaarsregelen" }
        }

        val pensjonUtenDobbeltbeskatningsmetode = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(pensjonsinntekt, perMaaned))
            .harIngenForekomsterDer { fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) erLik "ettaarsregelen" }
        }

        // kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for skattenedsettelse etter ettårsregelen
        // og IKKE har oppgitt metode for dobbelbeskatning = ettårsregelen på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen)
            .utslagFor {

                val harKravOmEttaarsregelen = hvorMinstEnForekomst {
                    (startdatoForArbeidsoppholdIUtland er oppgitt) eller
                        (sluttdatoForArbeidsoppholdIUtland er oppgitt) eller
                        (antallOppholdsdagerINorge er oppgitt)
                }

                hvis(
                    harKravOmEttaarsregelen og
                        loennUtenDobbeltbeskatningsmetode og pensjonUtenDobbeltbeskatningsmetode
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

       val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

       val kortUtenEndringsaarsakBosatt = skattemelding
            .forForekomsterAv{
                grupper(
                    arbeidsgiver,
                    pensjonsinntekt,
                    konto,
                    aksjeIkkeIAksjonaerregisteret,
                    aksjeIAksjonaerregisteret,
                    verdipapirfond,
                    obligasjonOgSertifikat,
                    aksjesparekonto,
                    annetFinansprodukt,
                    skattefavorisertIndividuellSparingTilPensjon,
                    realisasjonAvFastEiendom,
                    sjablongbehandletUtleieIUtlandet,
                    regnskapsbehandletUtleieIUtlandet,
                    annenKapitalinntekt,
                    formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                    formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                    formuesgrunnlagBoenhetIBoligselskap,
                    formuesgrunnlagBorett,
                    formuesgrunnlagEgenFritaksbehandletBolig,
                    formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                    formuesgrunnlagFlerboligbygning,
                    formuesgrunnlagGaardsbruk,
                    formuesgrunnlagIkkeUtleidNaeringseiendomINorge,
                    formuesgrunnlagRegnskapsbehandletBolig,
                    formuesgrunnlagRegnskapsbehandletFritidseiendom,
                    formuesgrunnlagSelveidBolig,
                    formuesgrunnlagSelveidFritidseiendom,
                    formuesgrunnlagTomt,
                    formuesgrunnlagUtleidNaeringseiendom
                )
            }
                .forForekomsterAv{aarsakTilEndring}
                .harIngenForekomsterDer { aarsak erLik "bosattEtterSkatteavtaleIAnnetLand" }

        // kontrollen gir utslag dersom skattyter har oppgitt tilleggssinformasjon om Bosatt etter skatteavtale i annet land,
        // men IKKE har oppgitt endringsårsak  "Jeg er bosatt etter skatteavtale i et annet land enn Norge" på noe kort
        // skal IKKE gi utslag hvis skatteplikt Begrenset
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale}
            .utslagFor {
                val harTilleggsinfoOmBosatt = hvorMinstEnForekomst {
                        (bosattILandEtterSkatteavtale er oppgitt) eller
                        (startdatoForBosattIUtlandEtterSkatteavtale er oppgitt) eller
                        (sluttdatoForBosattIUtlandEtterSkatteavtale er oppgitt) eller
                        (betalerSkattTilBosattlandEtterSkatteavtale er oppgitt)
                }

            hvis(ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                    harTilleggsinfoOmBosatt og (kortUtenEndringsaarsakBosatt)) 
              {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
              }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag og merknad dersom utgifter til Doktorgrad (middag, trykk og reise) overstiger kr 28 540
        // og Sum kostnader som inngår i minstefradrag > minstefradrag
        skattemelding
            .forForekomsterAv{minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt}            
            .utslagFor {
                val sumKostnaderTilDoktorgradmiddag = forForekomsterAv{kostnad}
                    .filtrer{fastsatt(kostnadstype).finnesBlant("middagKnyttetTilDoktorgrad", "trykkEllerReiseKnyttetTilDoktorgrad") }
                    .summer{fastsatt(beloep.beloep)}

                val sumAndreKostnaderArbeidsinntektMinusMinstefradrag = 
                    summer{fastsatt(fradragForKostnaderKnyttetTilArbeid.beloepUtenHensynTilValgtPrioritertFradragstype.beloep) -
                           fastsatt(minstefradragIInntekt.beloepUtenHensynTilValgtPrioritertFradragstype.beloep)}    

                hvis((sumKostnaderTilDoktorgradmiddag erStørreEnn 28_540) og
                     (sumAndreKostnaderArbeidsinntektMinusMinstefradrag erStørreEnn 0)
              ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

       // hvis sluttbruker har lagt inn andre kostnader knyttet til annen inntekt UTEN beskrivelse av kostnadene
       skattemelding
            .forForekomsterAv{minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt}
            .utslagFor {

                val harOppgittAndreKostnaderAnnenInntektUtenBeskrivelse = forForekomsterAv{kostnad}
                   .hvorMinstEnForekomst {
                      (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilAnnenInntekt") og 
                      (fastsatt(beloep.beloep) erStørreEnn 0) og
                      (fastsatt(beskrivelse) ikkeEr oppgitt)}

                hvis(harOppgittAndreKostnaderAnnenInntektUtenBeskrivelse) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harUtslagPaaKontroll = utslagPåKontroll("ANNEN_GJELD_RESTSKATT") eller
                                 utslagPåKontroll("ANNEN_GJELD_NAV") eller
                                 utslagPåKontroll("ANNEN_GJELD_SKATTEETATEN") eller
                                 utslagPåKontroll("ANNEN_GJELD_INKASSO") eller
                                 utslagPåKontroll("ANNEN_GJELD_STATENS_INNKREVINGSSENTRAL")

      val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

     // Kontrollen gir utslag dersom krever fradrag for renter på annen gjeld større enn eller lik 20 000
     // og Renter < 10 % av gjelden
     // AB-test: Gir utslag hvis Skattepliktig har følgende i siffer syv i partsnummeret: 0, 1, 2 
     //skal ikke gi utslag hvis utslag på annen kontroll 
     //skal ikke gi utslag hvis skattyter har lagt ved dokumentasjon
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{annenGjeld}
            .utslagFor {

                val harOppgittAnnenGjeldStoerreEnnBeloepsgrense = hvorMinstEnForekomst{
                   (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erStørreEnnEllerLik 20_000) og
                   (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erMindreEnn (fastsatt(gjeldsbeloep.beloep) * 10.prosent))              
                }

                val harVedlegg = hvorMinstEnForekomst{paaloepteRenter.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                hvis(ikke(harUtslagPaaKontroll) og
                     (partsnr.tegnFraIndeks(7).finnesBlant("0", "1", "2")) og
                     harOppgittAnnenGjeldStoerreEnnBeloepsgrense og
                     ikke(harVedlegg)) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
                }
            }
        }

