sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harUtslagPaaKontroll = utslagPåKontroll("ANNEN_GJELD_RESTSKATT") eller
                                 utslagPåKontroll("ANNEN_GJELD_NAV") eller
                                 utslagPåKontroll("ANNEN_GJELD_SKATTEETATEN") eller
                                 utslagPåKontroll("ANNEN_GJELD_INKASSO") eller
                                 utslagPåKontroll("ANNEN_GJELD_STATENS_INNKREVINGSSENTRAL")

      val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

     // Kontrollen gir utslag dersom krever fradrag for renter på annen gjeld større enn eller lik 20 000
     // og Renter < 10 % av gjelden
     // AB-test: Gir utslag hvis Skattepliktig har følgende i siffer syv i partsnummeret: 3, 4, 5, 6 
     //skal ikke gi utslag hvis utslag på annen kontroll 
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{annenGjeld}
            .utslagFor {

                val harOppgittAnnenGjeldStoerreEnnBeloepsgrense = hvorMinstEnForekomst{
                   (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erStørreEnnEllerLik 20_000) og
                   (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erMindreEnn (fastsatt(gjeldsbeloep.beloep) * 10.prosent))              
                }

                hvis(ikke(harUtslagPaaKontroll) og
                     (partsnr.tegnFraIndeks(7).finnesBlant("3", "4", "5", "6")) og
                     harOppgittAnnenGjeldStoerreEnnBeloepsgrense ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

