sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har sykepenger eller dagpenger, og har arbeidsreiser med tilsammen 200 dager eller mer - gir merknad på arbeidsreisene
        val harBehandlingsartSykepengerDagpenger = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                (behandlingsart erLik "DAGPENGER") eller (behandlingsart erLik "SYKEPENGER")
            }
        }

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(reiserIArbeidsforhold)
            .utslagFor {
                val sumAntallArbeidsreisedager = forForekomsterAv(arbeidsreise)
                    .summer { antallReiser }

                hvis((sumAntallArbeidsreisedager erStørreEnnEllerLik 200) og (harBehandlingsartSykepengerDagpenger)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harHoeyeFradragFormerkostnader = skattemelding
            .forForekomsterAv{arbeidsoppholdUtenforHjemmet}
            .hvorMinstEnForekomst {
                fradragForMerkostnadVedArbeidsoppholdUtenforHjemmet.fradragsberettigetBeloep.beloep erStørreEnn 10_000
            }

        // kontrollen gir utslag dersom skattyter har fradrag for merkostnader ved kost og losji på mer enn 10 000 og har lagt inn overnattingsdøgn
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{
                grupper(
                    pendleropphold.spesifikasjonAvPendlerbolig,
                    oppholdUnderYrkesOgTjenestereise,
                    oppholdUnderKjoereoppdrag
                )
            }
            .utslagForFørste {

                val harOvernattingsdoegn = hvorMinstEnForekomst{antallOvernattingsdoegn erStørreEnn 0}  

                hvis(harOvernattingsdoegn og
                     harHoeyeFradragFormerkostnader
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

    val fjoraaretsForekomster = fjoraaretsSkattemelding
       .forForekomsterAv(inntektsaar2022.aksjeIAksjonaerregisteret)
       .filtrer{
         (fastsatt(inntektsaar2022.isinnummer) er oppgitt) og (fastsatt(inntektsaar2022.selskapetsNavn) er oppgitt) og(
           (fastsatt(inntektsaar2022.skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) erStørreEnn 0) eller
           (fastsatt(inntektsaar2022.skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0)
         )
       }

    val aaretsForekomster = utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .forForekomsterAv(aksjeIAksjonaerregisteret)
        .filtrer {
            (fastsatt(isinnummer) er oppgitt) og (fastsatt(selskapetsNavn) er oppgitt) og
                ((fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) erStørreEnn 0) eller
                    (fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0))
        }
    }

    val aaretsOgFjoraaretsForekomster = aaretsForekomster + fjoraaretsForekomster

    //kontrollen gir utslag hvis skattyter oppgir nytt aksjekort med gevint eller tap på aksje (finnes ikke grunnlag)
    //med samme selskapsnavn og isinnummer som et annet kort i årets skattemelding eller i fjorårets skattemelding
    //gir kun utslag hvis oppgitt gevinst/tap er større enn 100 000
    utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .fokusområde("Fokusområde")
        .forForekomsterAv(aksjeIAksjonaerregisteret)
        .utslagForFørste {
            val isin = verdiFor { fastsatt(isinnummer) }
            val navnPaaAksjeselskap = verdiFor { fastsatt(selskapetsNavn) }

            val harGevinstEllerTapOver100000 = hvorMinstEnForekomst {
                ((grunnlag(isinnummer) ikkeEr oppgitt) og (fastsatt(isinnummer) er oppgitt)) og
                    ((grunnlag(selskapetsNavn) ikkeEr oppgitt) og (fastsatt(selskapetsNavn) er oppgitt)) og (
                    (fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) erStørreEnn 100_000) eller
                        (fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 100_000)
                    )
            }

            val lagtTilIaarOgEllerIFjor = aaretsOgFjoraaretsForekomster
                .hvorMerEnnEnForekomst {
                    val lagtTilIFjor = (
                        (fastsatt(inntektsaar2022.isinnummer) erLik isin) og (fastsatt(inntektsaar2022.selskapetsNavn) erLik navnPaaAksjeselskap) og (
                            (fastsatt(inntektsaar2022.skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) erStørreEnn 0) eller
                                (fastsatt(inntektsaar2022.skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0)
                            )
                        )
                    val lagtTilIAar = (
                        (fastsatt(isinnummer) erLik isin) og (fastsatt(selskapetsNavn) erLik navnPaaAksjeselskap) og
                            ((fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) erStørreEnn 0) eller
                                (fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0))
                        )
                    lagtTilIFjor eller lagtTilIAar
                }

            hvis(ikke(harInnsending) og harGevinstEllerTapOver100000 og lagtTilIaarOgEllerIFjor)
            {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
            }
        }
    }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt formue på aksjer i AR er ulik grunnlag med 100 mill eller mer - kun merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .utslagFor {
                val differanseFormue = differanse(
                    fastsatt(verdiFoerVerdsettingsrabattForAksje.beloepSomTall),
                    grunnlag(verdiFoerVerdsettingsrabattForAksje.beloepSomTall)
                )

                hvis(ikke(harInnsending) og (differanseFormue erStørreEnnEllerLik 100_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        Avviksbeloep settesTil differanseFormue
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // gir utslag dersom oppgitt gevinst på aksjer i AR er ulik grunnlag med 20 mill eller mer
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .utslagFor {
                val differanseGevinst = differanse(
                    fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall),
                    grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall)
                )

                hvis(differanseGevinst erStørreEnnEllerLik 20_000_000) {
                    betjeningsstrategiSettesTil(
                        myndighetsfastsettingMedVarselOgVedtak, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        Avviksbeloep settesTil differanseGevinst
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt gevinst på aksje i AR er midre enn grunnlag

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) er oppgitt }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall
                        )
                        )


                hvis(differanseGevinst erStørreEnn 50_000) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,

                        )
                }
            }
        }
    }

