sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

       val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = skattemelding
            .forForekomsterAv{aksjeIAksjonaerregisteret}
            .forForekomsterAv{aarsakTilEndring}
            .harIngenForekomsterDer { fastsatt(aarsak) erLik "overfortTilFraEktefelle" }

        // Gir utslag dersom oppgitt/fastsatt utbytte på aksje i AR er midre enn grunnlag
        // og har ikke oppgitt at det er overført fra ektefelle/samboer    
        // AB-test: gir utslag på skattytere som har partall i 7.siffer i partsnummeret
        skattemelding
            .forForekomsterAv{aksjeIAksjonaerregisteret}
            .filtrer { grunnlag(skattepliktigUtbytte.beloep) er oppgitt }
            .utslagFor {

                val harDifferanseUtbytte = hvorMinstEnForekomst{(grunnlag(skattepliktigUtbytte.beloep) - fastsatt(skattepliktigUtbytte.beloep)) erStørreEnn 10_000}


                hvis( (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og
                     oppgittEndringsaarsakIkkeErOverførtFraEktefelle og 
                     harDifferanseUtbytte) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt utbytte på aksjer i AR er ulik grunnlag med 10 mill eller mer
        skattemelding
            .forForekomsterAv{aksjeIAksjonaerregisteret}
            .utslagFor {
                val differanseUtbytte = verdiFor{differanse(
                    fastsatt(skattepliktigUtbytte.beloep),
                    grunnlag(skattepliktigUtbytte.beloep)
                ) }

                hvis(differanseUtbytte erStørreEnnEllerLik 10_000_000) {
                    betjeningsstrategiSettesTil(
                        hentesFraOppgavetypedefinisjonen, merknadStandard, 
                        Avviksbeloep settesTil differanseUtbytte
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Gir utslag dersom oppgitt/fastsatt tap på aksje i AR er større enn grunnlag (økning preutfylt tap eller lagt til egenfastsatt tap)
        skattemelding
            .forForekomsterAv{aksjeIAksjonaerregisteret}
            .utslagFor {
                val differanseTapStoerreEnnBeloepsgrense = hvorMinstEnForekomst{(fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloep) - 
                                                          grunnlag(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloep)) erStørreEnnEllerLik 50_000}

                hvis(differanseTapStoerreEnnBeloepsgrense) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard   
                        )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt gevinst på aksjesparekonto er midre enn grunnlag
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjesparekonto)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjon.beloepSomTall) er oppgitt }
            .utslagFor {
                val differanseGevinst = (
                    grunnlag(skattepliktigGevinstVedRealisasjon.beloepSomTall) - fastsatt(
                        skattepliktigGevinstVedRealisasjon.beloepSomTall
                    )
                    )

                val harVedlegg =
                    forForekomsterAv(skattepliktigGevinstVedRealisasjon).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                // Skal kun gi merknad i SME fra 1 000 til 20 mill (for å unngå flere merknader/oppgaver på samme beløp)
                hvis(ikke(harVedlegg) og ((differanseGevinst erStørreEnnEllerLik 1_000) og (differanseGevinst erMindreEnn 20_000_000))) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt formue på aksjesparekonto er mindre enn grunnlag
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjesparekonto)
            .filtrer {
                (grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) er oppgitt) eller (
                    grunnlag(
                        formuesverdiForKontanter.beloepSomTall
                    ) er oppgitt
                    )
            }
            .utslagFor {
                val differanseFormueFoerVerdsettingsrabatt =
                    (
                        grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) - fastsatt(
                            verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall
                        )
                        )
                val differanseFormueKontanter =
                    (grunnlag(formuesverdiForKontanter.beloepSomTall) - fastsatt(formuesverdiForKontanter.beloepSomTall))

                val harVedlegg =
                    forForekomsterAv(verdiFoerVerdsettingsrabattForAksjeOgAksjefond).hvorMinstEnForekomst { beloepIValuta har vedlegg } eller
                        forForekomsterAv(formuesverdiForKontanter).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                // Skal kun gi merknad i SME fra 500 000(for å unngå flere merknader/oppgaver på samme beløp)
                hvis(
                    ikke(harVedlegg) og
                        ((differanseFormueFoerVerdsettingsrabatt + differanseFormueKontanter) erStørreEnnEllerLik 500_000)

                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,

                        )
                }
            }
        }
    }

