sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn info besøksreise innenfor eller utenfor EØS
        // og ikke har lagt inn adresse og land for arbeidssted eller hjemsted (obligatoriske felter i SME)
       skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .utslagFor {

                val harOppgittBesoeksreiseInnenforEOESMedMangelfullAdresse = forForekomsterAv{besoeksreiseTilHjemInnenforEOeS}
                   .hvorMinstEnForekomst {
                      ((fastsatt(antallReiser) er oppgitt) eller
                      (fastsatt(lengdeAvReiseTurRetur) er oppgitt) eller
                      (fastsatt(utgiftTilFlybilletterForBesoeksreiserTilHjemmet.beloep) er oppgitt) eller
                      (fastsatt(egenandelVedReiseDekketAvArbeidsgiver.beloep) er oppgitt) eller
                      (fastsatt(underskuddVedUtbetaltGodtgjoerelseEllerRefusjon.beloep) er oppgitt) eller
                      (fastsatt(sparerMerEnnToTimerVedBrukAvEgenBil) er oppgitt) eller
                      (fastsatt(bompengeutgift.beloep) er oppgitt) eller
                      (fastsatt(fergeutgift.beloep) er oppgitt)) og  
                      ikke((fastsatt(reiseFraAdresse) er oppgitt) og (fastsatt(reiseFraSted) er oppgitt) og (fastsatt(reiseFraLand) er oppgitt) og
                           (fastsatt(reiseTilAdresse) er oppgitt) og (fastsatt(reiseTilSted) er oppgitt) og (fastsatt(reiseTilLand) er oppgitt)) }

                val harOppgittBesoeksreiseUtenforEOESMedMangelfullAdresse = forForekomsterAv{besoeksreiseTilHjemUtenforEOeS}
                   .hvorMinstEnForekomst {
                      ((fastsatt(antallReiser) er oppgitt) eller
                      (fastsatt(lengdeAvReiseTurRetur) er oppgitt) eller
                      (fastsatt(kostnaderVedBesoeksreiseTilHjemmetUtenforEOeS.beloep) er oppgitt) eller
                      (fastsatt(sparerMerEnnToTimerVedBrukAvEgenBil) er oppgitt) eller
                      (fastsatt(bompengeutgift.beloep) er oppgitt) eller
                      (fastsatt(fergeutgift.beloep) er oppgitt)) og  
                      ikke((fastsatt(reiseFraAdresse) er oppgitt) og (fastsatt(reiseFraSted) er oppgitt) og (fastsatt(reiseFraLand) er oppgitt) og
                           (fastsatt(reiseTilAdresse) er oppgitt) og (fastsatt(reiseTilSted) er oppgitt) og (fastsatt(reiseTilLand) er oppgitt)) }   

                hvis(harOppgittBesoeksreiseInnenforEOESMedMangelfullAdresse eller harOppgittBesoeksreiseUtenforEOESMedMangelfullAdresse) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .forForekomsterAv{besoeksreiseTilHjemUtenforEOeS}
            .utslagFor {
                val harVedlegg = hvorMinstEnForekomst{lengdeAvReiseTurRetur har vedleggPåFelt}

                val harReise = hvorMinstEnForekomst{lengdeAvReiseTurRetur erStørreEnn 0}
                hvis(ikke(harVedlegg) og harReise) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val betaltFesteavgiftIFjor = fjoraaretsSkattemelding
         .forForekomsterAv(inntektsaar2022.kapitalisertFesteavgift)
         .hvorMinstEnForekomst{fastsatt(inntektsaar2022.gjeldsfradragForKapitalisertFesteavgift.beloepSomTall) erStørreEnn 0}


        //kontrollen gir utslag dersom skattyter i fjor mottok festavgift,
        //men ikke har fått preutfylt eller egenfastsatt dette i år
        //kontrollen gir tips på kort for festavgift
        skattemelding
            .utslagFor {

            val harBetaltFestavgiftIAar = forForekomsterAv{kapitalisertFesteavgift}
                .hvorMinstEnForekomst { fastsatt(gjeldsfradragForKapitalisertFesteavgift.beloep) erStørreEnn 0 }

            hvis(betaltFesteavgiftIFjor og ikke(harBetaltFestavgiftIAar))
              {
                betjeningsstrategiSettesTil(ikke, merknadTips)
              }
            }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {

      val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        //kontrollen gir utslag og merknad dersom skattyter har oppgitt betalt skatt i utlandet
        //og det ikke er oppgitt noe annet kort med samme land og dobbeltbeskatningsmetode = kredit
        //skal IKKE gi utslag hvis utslag på kontroll SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING
        //skal IKKE gi utslag dersom skatteplikt er begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagFor {
                val landMedUtenlandskSkatt = verdiFor { landkode }

                val harKravOmKreditfradragForUtenlandskSkatt = hvorMinstEnForekomst {
                    (kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall erStørreEnn 0) eller
                        (kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall erStørreEnn 0) eller
                        (skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0) eller
                        (bruttoAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0)
                }

                val eiendommerIUtlandetMedBetaltSkatt = skattemeldingUtgått
                    .forForekomsterAv(fastEiendom)
                    .filtrer { fastsatt(landkode) erLik landMedUtenlandskSkatt }
                    .verdierFor { internEiendomsidentifikator }

                val harIngenEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            realisasjonAvFastEiendom,
                            sjablongbehandletUtleieIUtlandet,
                            regnskapsbehandletUtleieIUtlandet,
                            formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                            formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                            formuesgrunnlagBoenhetIBoligselskap,
                            formuesgrunnlagBorett,
                            formuesgrunnlagEgenFritaksbehandletBolig,
                            formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                            formuesgrunnlagFlerboligbygning,
                            formuesgrunnlagGaardsbruk,
                            formuesgrunnlagIkkeUtleidNaeringseiendomIUtlandet,
                            formuesgrunnlagRegnskapsbehandletBolig,
                            formuesgrunnlagRegnskapsbehandletFritidseiendom,
                            formuesgrunnlagSelveidBolig,
                            formuesgrunnlagSelveidFritidseiendom,
                            formuesgrunnlagTomt,
                            formuesgrunnlagUtleidNaeringseiendom
                        )
                    )
                    .filtrer {
                        fastsatt(internEiendomsidentifikator).finnesBlant(
                            eiendommerIUtlandetMedBetaltSkatt
                        )
                    }
                    .forAlleForekomster
                    .harIngenForekomsterDer { fastsatt(metodeVedDobbeltbeskatning) erLik "kredit" }

                val harIngenAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            arbeidsgiver,
                            pensjonsinntekt,
                            konto,
                            aksjeIkkeIAksjonaerregisteret,
                            aksjeIAksjonaerregisteret,
                            verdipapirfond,
                            obligasjonOgSertifikat,
                            aksjesparekonto,
                            annetFinansprodukt,
                            skattefavorisertIndividuellSparingTilPensjon,
                            annenKapitalinntekt,
                            skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler,
                            virtuellEiendel,
                            deltakersAndelAvFormueOgInntekt
                        )
                    )
                    .filtrer { fastsatt(landkode) erLik landMedUtenlandskSkatt }
                    .forAlleForekomster
                    .harIngenForekomsterDer {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                val harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet)
                        .harIngenForekomsterDer { fastsatt(landkode) erLik landMedUtenlandskSkatt }

                val harIkkeUtflyttingsskattMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(spesifikasjonAvUtflyttingOgUtflyttingsskatt)
                        .harIngenForekomsterDer { fastsatt(landkode) erLik landMedUtenlandskSkatt }       

                val harIngenUtflyttingsskattMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt,
                            andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt,
                            opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt                            
                        )
                    )
                    .forForekomsterAv(gruppe(
                           realisasjonAvAksjeOgAnnetFinansprodukt,
                           realisasjonAvAndelISelskapMedDeltakerfastsetting,
                           realisasjonAvOpsjon))
                    .forAlleForekomster
                    .harIngenForekomsterDer {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }        

                hvis(
                    ikke(harUtslagPaaKontroll) og ikke(harBegrensetSkatteplikt) og
                        harKravOmKreditfradragForUtenlandskSkatt og
                        harIngenEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngenAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt og
                        harIngengrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil og 
                        harIkkeUtflyttingsskattMedLandDetErBetaltSkattTil og
                        harIngenUtflyttingsskattMetodeVedDobbeltbeskatningOppgitt
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1, skjulerSkatteberegning)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
           val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt endringsårsak = bosattEtterSkatteavtaleIAnnetLand på minst et kort
        // og ikke har oppgitt tilhørende tilleggsinformasjon under andre forhold
        // skal ikke gi utslag dersom metode er valgt
        // skal IKKE gi utslag hvis skatteplikt Begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val kortMedEndringsaarsak = forAlleForekomster
                .hvorMinstEnForekomst { aarsak erLik "bosattEtterSkatteavtaleIAnnetLand" }

            val kortMedDobbeltbeskatningsmetode = forAlleForekomster
                .hvorMinstEnForekomst { metodeVedDobbeltbeskatning er oppgitt }

            hvis(
                kortMedEndringsaarsak og ikke(harBegrensetSkatteplikt) og
                    ikke(kortMedDobbeltbeskatningsmetode) og
                    ikke(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er oppgitt)
            ) {
                betjeningsstrategiSettesTil(
                    ikke, merknadStandard, 1
                )
            }
        }
        }
    }

