sakstype(Personlig)
    .kontekst(AdHocLabel) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        // kontrollen gir utslag dersom skattyters partsnr finnes i kontrolllisten
        // de ligger i kontrollisten hvis det skal gis utvidet veilledning og veiledning på CRS og skattyter har egenfastsatt CRS opplysninger uten beløp
        // kontrollen gir veiledningsoppgave i SMIA og utvidet veiledning i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val verdiFraKontrolliste = hentKontrolliste(

                "kontrolliste - CRS Egenfastsatt uten beloep",
                listeAv(partsnr)
            ).forForekomsterAv { verdi }
                .alleVerdier

            hvis(verdiFraKontrolliste.antall erStørreEnn 0) {
                betjeningsstrategiSettesTil(
                    sendInfoOmUtvidetVeiledning,
                    visUtvidetVeiledning,
                    1,
                    spesialomraade,
                    Gruppering settesTil Finans,
                    ikkeObligatorisk,
                    KontonummerIKontrolliste settesTil verdiFraKontrolliste
                )
            }
        }
        }
    }

