sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harTrygdefritakISkatteplikt = skattepliktGjeldende
          .forForekomsterAv { skatteplikt }
          .hvorMinstEnForekomst{(personligSkattesubjekt.fritakForTrygdeavgiftsplikt erLik true) eller
                                (personligSkattesubjekt.kunTrygdeavgift erLik true)}

        //kontrollen gir utslag dersom skattyter har lønnsinntekt og ikke har oppgitt trygdefritak på noe lønn,
        //og skattyter i skatteplikt har kunTrygdeavgift eller fritakForTrygdeavgiftsplikt
        //kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

            val harIkkeOppgittTrygdefriLoenn = forForekomsterAv{arbeidsgiver}
                .forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                .filtrer { fastsatt(beloep.beloep) erStørreEnn 0 }
                .harIngenForekomsterDer {
                    fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                        "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                        "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattepliktigUtenTrygdeavgiftsplikt"
                    )
                }


            hvis(harTrygdefritakISkatteplikt og 
                 harIkkeOppgittTrygdefriLoenn)
            {
                betjeningsstrategiSettesTil(ikke, merknadInformasjon)
            }
        }
        }

