sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val harKontoIBantmedGittNavniUtkast = skattemelding
         .forForekomsterAv{konto}
         .hvorMinstEnForekomst{grunnlag(bankensNavn).inneholder("alektum")}

     // Kontrollen gir utslag dersom skattyter egenfastsetter gjeld eller gjeldsrenter i et nytt kort med navn som inneholder et spesifikt ord
     // og skattyter også har en konto i utkastet med det samme ordet i navnet
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{konto}
            .filtrer{(fastsatt(bankensNavn) er oppgitt) og (grunnlag(bankensNavn) ikkeEr oppgitt)}
            .utslagFor {

                val harEgenfastsattkontoMedGjeldEllerGjeldsrenterIBankMedGittNavn = hvorMinstEnForekomst{
                  (grunnlag(bankensNavn) ikkeEr oppgitt) og
                  (fastsatt(bankensNavn).inneholder("alektum")) og
                  ((fastsatt(gjeld.beloep) erStørreEnn 0) eller (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erStørreEnn 0))
