sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val sumEierandelAlleForekomsterGrunnlag = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fastEiendom)
            .summer { grunnlag(eierandel) }
        }

        val sumEierandelAlleForekomsterFastsatt = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fastEiendom)
            .summer { fastsatt(eierandel) }
        }

        val diffSumEierandel = differanse(sumEierandelAlleForekomsterGrunnlag, sumEierandelAlleForekomsterFastsatt)  

        // kontrollen gir utslag og tips dersom skattyter har redusert eierandel og realisasjon av eiendom ikke er lagt inn
        // for å unngå at den gir utslag dersom det bare fjernet en forekomst som lå i utlast og lagt til en tilsvarende i fastsatt,
        // legges det til et ekstrakriterie at denne kun skal gi utslag dersom det er diff i summen av eierandel på alle forekomster
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val harEndretEierandel = forForekomsterAv(fastEiendom)
                .hvorMinstEnForekomst { grunnlag(eierandel) erStørreEnn fastsatt(eierandel) }

            hvis(
                ikke(harInnsending) og
                    harEndretEierandel og
                    ikke(realisasjonAvFastEiendom er oppgitt) og
                    (diffSumEierandel erStørreEnn 0)
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadTips, 1)
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag på alle eiendommer hvor skattyter har endret eiendomstype fra bolig til fritidsbolig
        // skal kun gi utslag dersom formuesverdi er preutfylt på eiendommen

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                    formuesgrunnlagRegnskapsbehandletFritidseiendom,
                    formuesgrunnlagSelveidFritidseiendom
                )
            )
            .filtrer { grunnlag(formuesverdi.beloepSomTall) ikkeEr oppgitt }
            .utslagFor {
                val identifikator = verdiFor { fastsatt(internEiendomsidentifikator) }
                val endretFra = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            formuesgrunnlagSelveidBolig,
                            formuesgrunnlagBoenhetIBoligselskap,
                            formuesgrunnlagBorett
                        )
                    )
                    .filtrer { grunnlag(formuesverdi.beloepSomTall) er oppgitt }
                    .hvorMinstEnForekomst { grunnlag(internEiendomsidentifikator) erLik identifikator }
                hvis(ikke(harInnsending) og (internEiendomsidentifikator er oppgitt) og endretFra) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val boligForekomsterSomManglerArealEllerBoligtype = skattemelding
            .forForekomsterAv{grupper(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap)}
            .filtrer {
                (fastsatt(internEiendomsidentifikator) er oppgitt) og
                    (
                        (fastsatt(boligensAreal) ikkeEr oppgitt) eller
                            (fastsatt(boligtype) ikkeEr oppgitt) eller
                            (fastsatt(byggeaar) ikkeEr oppgitt)
                        )
            }

        val eiendommer = skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer { fastsatt(eierandel) erStørreEnn 0 }
            .verdierFor { internEiendomsidentifikator }

        val boligForekomsterMedEierandelSomManglerArealEllerBoligtype = boligForekomsterSomManglerArealEllerBoligtype
                .hvor { internEiendomsidentifikator finnesBlant eiendommer }

        // Kontrollen gir utslag på alle eiendommer som skattyter har eienandel større enn 0, som mangler areal, boligtype eller byggeår
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {
            hvis(
                boligForekomsterMedEierandelSomManglerArealEllerBoligtype
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadMangel
                )
            }
        }
        }

