sakstype(Personlig)
    .kontekst(ValideringFoerBeregning){

        // Kontrollen gir utslag hvis det finnes minst én forekomst av fast eiendom i Norge uten SERG ID 
        // skal ikke gi utslag på skogeiendom
        skattemelding
            .utslagFor {          

              val skogseiendomGaardsnummer = forForekomsterAv{formuesgrunnlagSkogeiendomINorge}
                 .verdierFor{gaardsnummer}

              val skogseiendomBruksnummer = forForekomsterAv{formuesgrunnlagSkogeiendomINorge}
                 .verdierFor{bruksnummer}   

              val harFastEiendomUtenSergId = forForekomsterAv{fastEiendom}
                  .filtrer{((fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt)) og
                           (fastsatt(gaardsnummer).finnesIkkeBlant(skogseiendomGaardsnummer)) og
                           (fastsatt(bruksnummer).finnesIkkeBlant(skogseiendomBruksnummer))}
                  .hvorMinstEnForekomst{(fastsatt(internEiendomsidentifikator) er oppgitt)  og
                                        (fastsatt(sergEiendomsidentifikator) ikkeEr oppgitt)}


                hvis(harFastEiendomUtenSergId) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
    }

