sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val eiendomINorge = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fastEiendom)
            .filtrer { (fastsatt(landkode) ikkeEr oppgitt) eller (fastsatt(landkode) erLik "NO") }
            .verdierFor { internEiendomsidentifikator }
        }

        // Kontrollen gir utslag og merknad dersom skattyter har en eiendom i Norge som Tilhører barn
        // og barnets fødselsnummer ikke er oppgitt - kun fødselsdato (må ha minst 11 tegn)
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(
            gruppe(
                formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                formuesgrunnlagBoenhetIBoligselskap,
                formuesgrunnlagBorett,
                formuesgrunnlagEgenFritaksbehandletBolig,
                formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                formuesgrunnlagFlerboligbygning,
                formuesgrunnlagForSkalIkkeFastsettes,
                formuesgrunnlagGaardsbruk,
                formuesgrunnlagIkkeUtleidNaeringseiendomINorge,
                formuesgrunnlagRegnskapsbehandletBolig,
                formuesgrunnlagRegnskapsbehandletFritidseiendom,
                formuesgrunnlagSelveidBolig,
                formuesgrunnlagSelveidFritidseiendom,
                formuesgrunnlagTomt,
                formuesgrunnlagUtleidNaeringseiendom
            )
        )
            .utslagFor {

                val harEiendomSomTilhoererBarnUtenFnr = hvorMinstEnForekomst {
                    (formuesverdi.beloepSomTall er oppgitt) og
                        (overfoertFraBarn.norskPersonidentifikator er oppgitt) og
                        (overfoertFraBarn.norskPersonidentifikator.formatert.lengde erUlik 11) og
                        (internEiendomsidentifikator.finnesBlant(eiendomINorge))
                }

                hvis(harEiendomSomTilhoererBarnUtenFnr) {
                    betjeningsstrategiSettesTil(ikke, merknadMangel, 1, skjulerSkatteberegning)
                }
            }
        }
    }

