sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) 
     { 

       val egenfastsatteEiendommerIUtlandet = skattemelding
           .forForekomsterAv{fastEiendom}
           .filtrer{(grunnlag(internEiendomsidentifikator) ikkeEr oppgitt) og
                    (fastsatt(internEiendomsidentifikator) er oppgitt) og
                    (fastsatt(landkode) er oppgitt) og
                    (fastsatt(landkode) erUlik "NO")}
           .verdierFor{fastsatt(internEiendomsidentifikator)}

        // Gir utslag på SP som legger til en eiendom med landkode som ikke er Norge
        // og eiendomstypen er noe annet enn en av de som er gyldige for utenlandske eiendommer
        //    borett
        //    egenFritaksbehandletBolig
        //    regnskapsbehandletBolig
        //    egenFritaksbehandletFritidseiendom
        //    regnskapsbehandletFritidseiendom
        //    tomt
        //    gaardsbruk
        //    skogeiendomIUtlandet
        //    ikkeUtleidNaeringseiendomIUtlandet
        //    annenFastEiendomInnenforInntektsgivendeAktivitet
        //    annenFastEiendomUtenforInntektsgivendeAktivitet
        //    utleieFlerboligbygningIUtlandet
        //    naeringseiendom (formuesgrunnlagUtleidNaeringseiendom)
        skattemelding
            .forForekomsterAv{grupper(formuesgrunnlagBoenhetIBoligselskap,
                                     formuesgrunnlagFlerboligbygning,
                                     formuesgrunnlagIkkeUtleidNaeringseiendomINorge,
                                     formuesgrunnlagSelveidBolig,
                                     formuesgrunnlagSelveidFritidseiendom,
                                     formuesgrunnlagSkogeiendomINorge)}
            .utslagFor {

                val harOppgittFeilTypeutenlandskEiendom = hvorMinstEnForekomst{(fastsatt(internEiendomsidentifikator) er oppgitt) og 
                                                               (fastsatt(internEiendomsidentifikator).finnesBlant(egenfastsatteEiendommerIUtlandet))}


                hvis(harOppgittFeilTypeutenlandskEiendom) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil
                    )
                }
            }
    }

