sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
           .forForekomsterAv { utvidetveiledning }
           .forForekomsterAv { metadata }
           .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }        

         val harUtleidFjoraaret = fjoraaretsSkattemelding
             .forForekomsterAv(
                 gruppe(
                     inntektsaar2022.sjablongbehandletUtleie,
                     inntektsaar2022.regnskapsbehandletUtleie,
                     inntektsaar2022.sjablongOgRegnskapsbehandletUtleie))
             .hvorMinstEnForekomst {
                 ((fastsatt(inntektsaar2022.bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall) erStørreEnn 0) eller 
                  (fastsatt(inntektsaar2022.bruttoLeieinntektVedRegnskapsbehandletUtleie.beloepSomTall) erStørreEnn 0)) }

        // Kontrollen gir utslag dersom skattyter hadde utleie i fjor, men ikke har oppgitt noen utleie i år,
        // Skal ikke gi utslag hvis skattyyter har fått produsert utvidet veileding (og vist det eller et tilhørende tips)    
        skattemelding
             .utslagFor {

             val harSjablongbehandletUtleie = forForekomsterAv{
                 grupper(
                     sjablongbehandletUtleie,
                     sjablongOgRegnskapsbehandletUtleie
                 )
             }
                 .hvorMinstEnForekomst {
                     fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) erStørreEnn 0
                 }

             val harRegnskapsbehandletUtleie = forForekomsterAv{
                 grupper(
                     regnskapsbehandletUtleie,
                     sjablongOgRegnskapsbehandletUtleie
                 )
             }
                 .hvorMinstEnForekomst {
                     fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep) erStørreEnn 0
                 }     

             hvis(
                 ikke(harUtvidetVeiledningPaaUtleie) og
                     ikke(harSjablongbehandletUtleie) og
                     ikke(harRegnskapsbehandletUtleie) og
                     harUtleidFjoraaret
             )
             {
                 betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
             }
         }
    }

sakstype(Personlig)
