        // Gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fondskonto)
            .utslagFor {
                val differanseGevinstAksjedel =
                    differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloepSomTall),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloepSomTall)
                    )
                val differanseGevinstRentedel =
                    differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloepSomTall),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloepSomTall)
                    )

                hvis(ikke(harInnsending) og ((differanseGevinstAksjedel + differanseGevinstRentedel) erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

