sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Kontrollen gir utslag dersom skattyter har oppgitt forvaltningskostnader over kr 50 000 uten at det finnes grunnlag
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(forvaltningskostnader)
            .utslagFor {
                val harDifferanseAarsavgiftIVPS = hvorMinstEnForekomst {
                    (grunnlag(aarsavgiftIVPS.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(aarsavgiftIVPS.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    aarsavgiftIVPS.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            )
                }

                val harDifferanseLeiekostnaderForBankboks = hvorMinstEnForekomst {
                    (grunnlag(leiekostnaderForBankboks.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(leiekostnaderForBankboks.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    leiekostnaderForBankboks.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            )
                }

                val harDifferanseForvaltningskostnaderForVerdipapirer = hvorMinstEnForekomst {
                    (grunnlag(forvaltningskostnaderForVerdipapirer.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(forvaltningskostnaderForVerdipapirer.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    forvaltningskostnaderForVerdipapirer.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            )
                }

                val harVedleggAarsavgiftIVPS =
                    forForekomsterAv(aarsavgiftIVPS.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepSomTall har vedlegg }

                val harVedleggLeiekostnaderForBankboks =
                    forForekomsterAv(leiekostnaderForBankboks.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepSomTall har vedlegg }

                val harVedleggForvaltningskostnaderForVerdipapirer =
                    forForekomsterAv(forvaltningskostnaderForVerdipapirer.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepSomTall har vedlegg }

                hvis(
                    ikke(harInnsending) og (ikke(harVedleggAarsavgiftIVPS) og harDifferanseAarsavgiftIVPS) eller
                        (ikke(harVedleggLeiekostnaderForBankboks) og harDifferanseLeiekostnaderForBankboks) eller
                        (ikke(harVedleggForvaltningskostnaderForVerdipapirer) og harDifferanseForvaltningskostnaderForVerdipapirer)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
        }
    }

